package googlejam8.p011;


import java.io.*;
import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;

public class TaskA {
    public static void main(String[] args) {
        String fileName = "A-large.in";
        try (BufferedReader br = new BufferedReader(new FileReader(fileName))) {

            try (BufferedWriter bw = new BufferedWriter(new FileWriter(fileName + ".out"))) {
                int tests = Integer.valueOf(br.readLine());
                for (int test = 1; test <= tests; ++test) {
                    int n = new Scanner(br.readLine()).nextInt();
                    Scanner scanner = new Scanner(br.readLine());
                    int s[] = new int[n];
                    for (int i = 0; i < n; ++i)
                        s[i] = scanner.nextInt();
                    bw.write("Case #" + test + ": ");
                    while (true) {
                        int max = 0;
                        for (int ss: s)
                            max = Math.max(max, ss);
                        if (max == 0)
                            break;
                        ArrayList<Integer> pos = new ArrayList<Integer>();
                        for (int i = 0; i < n; ++i) {
                            if (s[i] == max)
                                pos.add(i);
                        }
                        int ps = pos.size();
                        if (pos.size() % 2 == 1) {
                            ps--;
                            int cur = pos.get(pos.size() - 1);
                            s[cur]--;
                            bw.write("" + (char)('A' + cur) + " ");
                        }
                        for (int i = 0; i < ps; i += 2) {
                            int c1 = pos.get(i);
                            int c2 = pos.get(i + 1);
                            s[c1]--;
                            s[c2]--;
                            bw.write("" + (char)('A' + c1) + (char)('A' + c2) + " ");
                        }
                    }
                    bw.write("\n");
                }
                bw.close();
            }

        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

package googlejam8.p277;
//package codejam.codejam2016.round1c;

import java.io.*;
import java.util.*;

public class A {
  static final Scanner scanner = new Scanner(new BufferedReader(new InputStreamReader(System.in)));
  static final PrintStream out = System.out;

  public static void main(String[] args) {
    int T = scanner.nextInt();
    for (int t = 1; t <= T; t++) {
      out.print("Case #" + t + ": ");
      int N = scanner.nextInt();
	final int[] counts = new int[N];

	for (int i = 0 ; i < N; i++) {
	  counts[i] = scanner.nextInt();
	}

	PriorityQueue<Integer> pq = new PriorityQueue<>(N, new Comparator<Integer>() {
	  @Override
	  public int compare(Integer i1, Integer i2) {
	    return counts[i2] - counts[i1];
	  }
	});

	for (int i = 0; i < N; i++) {
	  pq.add(i);
	}

	while (!pq.isEmpty()) {
	  int p1 = pq.poll();

	  if (counts[p1] == 1 && pq.size() == 2) {
	    out.print((char) ('A' + p1));
	    out.print(' ');
	    counts[p1]--;
	    continue;
	  }

	  if (counts[pq.peek()] == counts[p1]) {
	    int p2 = pq.poll();
	    out.print((char) ('A' + p1));
	    out.print((char) ('A' + p2));
	    out.print(' ');

	    counts[p1]--;
	    counts[p2]--;

	    if (counts[p1] > 0) {
	      pq.add(p1);
	    }

	    if (counts[p2] > 0) {
	      pq.add(p2);
	    }
	  } else {
	    out.print((char) ('A' + p1));
	    out.print(' ');

	    counts[p1]--;

	    if (counts[p1] > 0) {
	      pq.add(p1);
	    }
	  }
	}

	out.println();
    }
  }
}

