			}
			while(total > 3)
			{
				int largest = 0;
				int second = 0;
				for(int i = 0; i < sens.length; i++)
				{
					if(sens[i] > sens[largest])
					{
						largest = i;
						second = -1;
					}
					else if(sens[i] == sens[largest] && i != 0)
					{
						second = i;
					}
				}
				ans += " " + (char)(65 + largest);
				if(second == -1)
				{
					ans += (char)(65 + largest);
					sens[largest] -= 2;
				}
				else
				{
					ans += (char)(65 + second);
					sens[second] -= 1;
					sens[largest] -= 1;
				}
				total -= 2;
			}
			if(total == 3)
			{
				int place = -1;
				for(int i = 0; i < sens.length; i++)
					if(sens[i] == 1)
					{
						place = i;
						sens[i] = 0;
