			N = Integer.parseInt(in.readLine());
			st = new StringTokenizer(in.readLine());
			P = new int[N];
			for (int i = 0; i < N; i++) P[i]=Integer.parseInt(st.nextToken()); 
			buf.format("Case #%d:", c);
			String s;
			while(true){
				int su = 0;
				for (int i = 0; i < P.length; i++) {
					su = su + P[i];
				}
				if (su==3){
					int m=0;
					for (int i = 1; i < P.length; i++) {
						if (P[i]>P[m]) m=i;
					}
					if (P[m]==0) break;
					s = ""+(char)('A'+m);
					buf.format(" %s", s);
					P[m]--;
					continue;
				}
				int m=0;
				for (int i = 1; i < P.length; i++) {
					if (P[i]>P[m]) m=i;
				}
				if (P[m]==0) break;
				s = ""+(char)('A'+m);
				buf.format(" %s", s);
				P[m]--;
				m=0;
				for (int i = 1; i < P.length; i++) {
					if (P[i]>P[m]) m=i;
				}
				if (P[m]==0) break;
				s = ""+(char)('A'+m);
				P[m]--;
				buf.format("%s", s);
			}
			buf.format("\n");
		}
		System.out.print(sb.toString());
	}

}

