		    		max=-1;pos1=-1;pos2=-1;

		        		for(i=0;i<n;i++){
		        			if(A.a[i]>max){
		        				max = A.a[i];
		        				pos1=i;
		        			}
		        		}
		        		//2nd
		        		for(i=0;i<n;i++){
		        			if(A.a[i] == max && i!=pos1){
		        				pos2=i;
		        			}
		        		}
		        	if(pos2>-1 && A.rem==3) pos2=-1;

		    		if(pos2>-1){
		    			bw.write(""+(char)('A'+pos1)+""+(char)('A'+pos2));
		    			A.a[pos1]--;
		    			A.a[pos2]--;
		    			A.rem-=2;
		    		} else {
		    			bw.write(""+(char)('A'+pos1));
		    			A.a[pos1]--;
		    			A.rem-=1;
		    		}

		        	if(A.rem>0) bw.write(" ");

		        	//check
		        	max=-1;
		        	for(i=0;i<n;i++){
			        	max = (A.a[i]>max)?A.a[i]:max;
		        	}
		        	if (max > A.rem/2) {
		        		System.out.println("Problem!");
		        	}
		    	}

		    	bw.write("\n");	
		    }
			bw.close();
		} finally {br.close();}
	}
}

