					int x = party[gold] - 1;
					int y = party[bronze];

					if ((x > y ? x : y) > (sum - 2) / 2) {

						sum--;
						party[gold]--;
						System.out.print((char) ((int) 'A' + gold));
					}
					else {

						sum -= 2;
						party[gold]--;
						party[silver]--;
						System.out.print((char) ((int) 'A' + gold));
						System.out.print((char) ((int) 'A' + silver));
					}

					if (sum == 0)
						System.out.println();
					else
						System.out.print(' ');
				}
		}

		scanner.close();
	}

}

