        PrintWriter pw = new PrintWriter("src/round1/roundC/A-large.out");
        int testCases = sc.nextInt();
     //   testCases=1;
        String alphabet="ABCDEFGHIJKLMNOPQRSTUVWXYZ";


        for (int casen = 0; casen < testCases; casen++) {
            pw.printf("Case #%d: ",casen+1);

            int n = sc.nextInt();
            int arr[]=new int[n];
            for (int i = 0; i < n; i++) {
                arr[i]=sc.nextInt();
            }
			int max1=0;
