import java.io.*;
import java.util.*;

public class A implements Comparator<A>, Comparable<A>{

    String name;
    int value;
    public A(char se,int value){
        this.name=""+se;
        this.value=value;
    }

    public static void main(String[] args) {
        Scanner in = new Scanner(new BufferedReader(new InputStreamReader(System.in)));
        int t = in.nextInt();

        for (int i = 1; i <= t; ++i) {
            int n = in.nextInt();
            System.out.print("Case #" + i + ":");
            char se='A';
            ArrayList<A> sensor=new ArrayList<A>();
            int total=0;
            for(int j=0;j<n;j++){
                int s=in.nextInt();

                A node=new A(se,s);
                se++;
                total+=s;
                sensor.add(node);
            }

            Collections.sort(sensor);
            while(sensor.get(0).value>0){

                if((double)sensor.get(1).value/(double)(total-1)<=0.5){
                    sensor.get(0).value--;
                    total--;
                    System.out.print(" "+sensor.get(0).name);
                }
                else {
                    sensor.get(0).value--;
                    sensor.get(1).value--;
                    total-=2;
                    System.out.print(" "+sensor.get(0).name+sensor.get(1).name);
                }
                //System.out.print(" total: " +total);
                Collections.sort(sensor);
            }
            System.out.println("");

        }

    }
}

package googlejam8.p213;

import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;
import java.io.PrintWriter;

public class SenateEvacuation {

	static PrintWriter writer;

	public static void main(String[] args) throws IOException {

		writer = new PrintWriter("out.txt");
		BufferedReader br = null;
		try {
			br = new BufferedReader(new FileReader("A-large (1).in"));
			String line = br.readLine();
		    int Num = Integer.parseInt(line);
		    for(int i = 0; i < Num; ++i) {
		    	line = br.readLine();
		    	int party = Integer.parseInt(line);
		    	line = br.readLine();
		    	String bufs[] = line.split(" ");
		    	int[] sena = new int[30];
		    	for(int j = 0; j < party; ++j){
		    		sena[j] = Integer.parseInt(bufs[j]);
		    	}

		    	String output = "";

		    	while(true) {
		    		int min = -1;
					int index1 = -1;
					int count = 0;
					int another1 = -1;
					for(int i1 = 0; i1 < party; ++i1) {
						if(sena[i1] > 0 &&(min == -1 || sena[i1] > min)) {
							min = sena[i1];
							index1 = i1;
							count = 1;
							another1 = -1;
						}
						else if(sena[i1] > 0 &&(min == -1 || sena[i1] == min)){
							count ++;
							another1 = i1;
						}
					}

					if(count == 2 && another1 > -1) {
						index1 += another1 * 100;
					}
					int index =  index1;
		    		if(index == -1) {
		    			break;
		    		}
		    		if(index < 30) { 
			    		sena[index] --;
			    		char a = (char) (index + 'A');
			    		output += (a + " ");
		    		}
		    		else {
		    			int another = index / 100;
		    			index = index % 100;

			    		sena[index] --;
			    		char a = (char) (index + 'A');
			    		char s = (char) (another + 'A');
			    		output += a;
			    		output += (s+ " ");
			    		sena[another] --;
		    		}
		    	}
		    	writer.println("Case #" + (i + 1) + ": " + output);
		    }
		} finally {
		    br.close();
			writer.close();
		}
	}

}

package googlejam8.p139;
import java.io.*;

import java.util.*;

public class codejam {

    static String i0 = "dummy.in";
    static String o0 = "dummy.out";

    static String i1 = "small.in";
    static String o1 = "small.out";

    static String i2 = "large.in";
    static String o2 = "large.out";

    public static void main(String[] args) throws Exception {

        Scanner r = null;
        FileWriter w = null;

        r = new Scanner(new FileReader(i0));
        w = new FileWriter(new File(o0));

        int t = r.nextInt();
        //r.nextLine();

        for (int i=0;i<t;i++){
            w.write("Case #"+(i+1)+": ");
            int n = r.nextInt();
            Map<String, Integer> mp = new HashMap<String, Integer>();
            int mn = 10001;
            for (int j=0;j<n;j++){
                int p = r.nextInt();
                if (mn > p)
                    mn = p;
                mp.put(String.valueOf((char)('A'+j)), p);
            }
            ArrayList<Map.Entry<String, Integer>> al = new ArrayList<Map.Entry<String, Integer>>(mp.entrySet());
            Collections.sort(al, (o1, o2) -> o2.getValue() - o1.getValue());
            boolean gothru = false;
            while(!gothru){
                gothru = true;
                for (int j=0;j<n;j++){
                    if (al.get(j).getValue() == mn)
                        continue;
                    else{
                        gothru = false;
                        w.write(al.get(j).getKey()+" ");
                        al.get(j).setValue(al.get(j).getValue()-1);
                    }
                }
            }
            for (int j=0;j<mn;j++){
                for (int k=0;k<n;k++){
                    if (k == n-2){
                        w.write(al.get(k).getKey());
                        w.write(al.get(k+1).getKey());
                        w.write(" ");
                        break;
                    } else {
                        w.write(al.get(k).getKey()+" ");
                    }
                }
            }
            //w.write(s1+" "+s2);
            w.write("\n");
        }
        w.close();
    }
}

