			int[] a = new int[p];
			StringTokenizer st = new StringTokenizer(in.readLine());
			for (int i = 0; i < p; i++)
				a[i] = Integer.parseInt(st.nextToken());
			StringBuilder ans = new StringBuilder();
			int max = 0;
			int max1 = 0;
			for (int n : a)
				max1 = Math.max(max1, n);
			while ((max = max1) != 0) {
				for (int i = 0; i < p; i++)
					if (a[i] == max) {
						a[i]--;
