		int T = s.nextInt();
		for (int t = 1; t <= T; t++) {
			out.write("Case #"+t+": "); 
			int N = s.nextInt();
			int[] parties = new int[N];
			int sum=0;
			for(int i=0; i<N; i++){
				parties[i]=s.nextInt();
				sum+=parties[i];
			}
			while(sum>0){
				int differentParties=0;
				int max=0;
				int iMax=-1;
				for(int i=0; i<N; i++){
					if(parties[i]>0)
						differentParties++;
					if(parties[i]>max){
						max=parties[i];
						iMax=i;
					}
				}
