					break;
				}
				if (onesLeft == 3) {
					for (int i = 0; i < N; i++) {
						if (senates[i] == 1) {
							senates[i]--;
							System.out.print((char) (65 + i) + " ");
							break;
						}
					}
					continue;
				}
				done = true;
				int max = 0;
				int maxNext = 0;
				for (int i = 0; i < N; i++) {
					if (senates[i] > 0) {
						done = false;
					}
					if (senates[i] >= senates[max]) {
						maxNext = max;
						max = i;
					}
				}
				if (done) {
					System.out.println();
					break;
				}
				if (senates[max] > senates[maxNext] || max == maxNext) {
					senates[max]--;
					System.out.print((char) (65 + max) + " ");
				} else {
					senates[max]--;
					senates[maxNext]--;
					System.out.print((char) (65 + max));
					System.out.print((char) (65 + maxNext) + " ");
				}
			}
		}
		sc.close();
	}

}

