	    			if(max < p[k][1]){
	    				max = p[k][1];
	    				maxp = k;
	    			}
	    		}
	    		int bv= p[j][1];
	    		int bp=p[j][0];
	    		p[j][1] = max;
	    		p[j][0] = p[maxp][0];
	    		p[maxp][0] = bp;
	    		p[maxp][1] = bv;
	    	}
	    	System.out.print("Case #" + i + ":");
	    	int ave = sum/n;
	    	int party = 0;
	    	while(sum != 0) {
	    		party = 0;
	    		boolean change = false;
	    		for(int j = 0; j < n; j++ ) {
	    			if(p[j][1] > ave) {
	    				p[j][1]--;
	    				sum --;
	    				char b = (char)p[j][0];
	    				System.out.print(" "+b);
	    				change = true;
	    			}
	    			if(p[j][1]!=0){
	    				party++;
	    			}
	    		}
	    		ave = sum/party;
	    		if(change==false) {
	    			for(int j = 0; j < n-2; j++) {
	    				while(p[j][1]!=0){
	    					p[j][1]--;
		    				char b = (char)p[j][0];
		    				System.out.print(" "+b);
	    				}
	    			}
	    			while(p[n-1][1]!=0){
	    				p[n-1][1]--;
	    				char b1 = (char)p[n-1][0];
	    				char b2 = (char)p[n-2][0];
