							}
						}
						else if(arr[i][j] == '<'){
							for(int k = j - 1; k >= 0; k--){
								if(arr[i][k] != '.'){
									found = true;
									break;
								}
							}
						}
						else if(arr[i][j] == 'v'){
							for(int k = i + 1; k < r; k++){
								if(arr[k][j] != '.'){
									found = true;
									break;
								}
							}
						}
						else if(arr[i][j] == '^'){
							for(int k = i - 1; k >= 0; k--){
								if(arr[k][j] != '.'){
									found = true;
									break;
								}
							}
						}
						if(found){
							continue;
						}
						boolean works = false;
						for(int k = 0; k < r; k++){
							if(k == i) continue;
							if(arr[k][j] != '.'){
								works = true;
								break;
							}
						}
						for(int k = 0; k < c; k++){
							if(k == j) continue;
							if(arr[i][k] != '.'){
								works = true;
								break;
							}
						}
						if(works){
							count++;
						}
						else{
							viable = false;
							break;
						}
					}
				}
				if(!viable) break;
			}
			if(viable){
				bw.write(count + "\n");
			}
			else{
				bw.write("IMPOSSIBLE\n");
			}

		}
		br.close();
		bw.close();
	}









}

package googlejam4.p047;
import java.io.File;
import java.io.FileNotFoundException;
import java.io.PrintWriter;
import java.util.Scanner;

public class Pegman {
    public static void main(String[] args) throws FileNotFoundException {
        Scanner in = new Scanner(new File("A-large.in"));
        PrintWriter out = new PrintWriter(new File("Pegman.out"));
//        Scanner in = new Scanner(System.in);
//        PrintWriter out = new PrintWriter(System.out);

        int[] dr = {0, 1, 0, -1};
        int[] dc = {1, 0, -1, 0};
        String lookup = ">v<^";
        int loops = in.nextInt();
        for (int loop = 0; loop < loops; loop++) {
            int r = in.nextInt();
            int c = in.nextInt();
            in.nextLine();

            char[][] arr = new char[r][];
            for (int i = 0; i < r; i++) {
                arr[i] = in.nextLine().toCharArray();
            }
            int ans = 0;
            big: for (int i = 0; i < r; i++) {
                out: for (int j = 0; j < c; j++) {
                    if (arr[i][j] != '.') {
                        int d = lookup.indexOf(arr[i][j]);
                        int a = i + dr[d];
                        int b = j + dc[d];
                        while(a >= 0 && b >= 0 && a < r && b < c) {
                            if(arr[a][b] != '.')
                                continue out;
                            a += dr[d];
                            b += dc[d];
                        }
                        ans++;
                        for (d = 0; d < 4; d++) {
                            a = i + dr[d];
                            b = j + dc[d];
                            while(a >= 0 && b >= 0 && a < r && b < c) {
                                if(arr[a][b] != '.')
                                    continue out;
                                a += dr[d];
                                b += dc[d];
                            }
                        }
                        ans = -1;
                        break big;
                    }
                }
            }


            out.printf("Case #%d: %s%n", loop + 1, ans >= 0 ? ans : "IMPOSSIBLE");
        }


        out.close();
        in.close();
    }
}

package googlejam4.p119;
import java.io.BufferedReader;
import java.io.InputStreamReader;

public class A2 {

	public static void main(String[] args) throws Exception {
		BufferedReader bf = new BufferedReader(new InputStreamReader(System.in));

		int ntests = Integer.parseInt(bf.readLine());
		for (int tc = 1; tc <= ntests; tc++) {
			String[] line = bf.readLine().split(" ");
			int r = Integer.parseInt(line[0]);
			int c = Integer.parseInt(line[1]);
			char[][] grid = new char[r][];
			for (int i = 0; i < r; i++) {
				grid[i] = bf.readLine().toCharArray();
			}

			boolean isImpossible = false;
			int numChanged = 0;
			int[][] dirs = { { -1, 0 }, { 0, 1 }, { 1, 0 }, { 0, -1 } };
			// this is n^3, but we could make it faster if we wanted
			for (int i = 0; i < r; i++) {
				for (int j = 0; j < c; j++) {
					int curDir = 0;
					switch (grid[i][j]) {
					case '.':
						// safe, nothing to do
						continue;
					case '^':
						curDir = 0;
						break;
					case '>':
						curDir = 1;
						break;
					case 'v':
						curDir = 2;
						break;
					case '<':
						curDir = 3;
						break;
					}

					boolean hasArrowInCurDir = false;
					for (int[] pos = { i + dirs[curDir][0], j + dirs[curDir][1] }; pos[0] >= 0 && pos[1] >= 0 && pos[0] < r
							&& pos[1] < c; pos[0] += dirs[curDir][0], pos[1] += dirs[curDir][1]) {
						if (grid[pos[0]][pos[1]] != '.') {
							hasArrowInCurDir = true;
							break;
						}
					}
					if (!hasArrowInCurDir) {
						for (int otherDirs = 0; otherDirs <= 3; otherDirs++) {
							if (otherDirs == curDir)
								continue;

							for (int[] pos = { i + dirs[otherDirs][0], j + dirs[otherDirs][1] }; pos[0] >= 0 && pos[1] >= 0
									&& pos[0] < r && pos[1] < c; pos[0] += dirs[otherDirs][0], pos[1] += dirs[otherDirs][1]) {
								if (grid[pos[0]][pos[1]] != '.') {
									hasArrowInCurDir = true;
									break;
								}
							}

							if (hasArrowInCurDir) {
								break;
							}
						}

						if (hasArrowInCurDir) {
							numChanged++;
						} else {
							isImpossible = true;
						}
					}
				}
			}

			if (isImpossible) {
				System.out.println("Case #" + tc + ": " + "IMPOSSIBLE");
			} else {
				System.out.println("Case #" + tc + ": " + numChanged);
			}
		}
	}
}

package googlejam4.p044;


import java.util.Scanner;

public class Main{
	static boolean[][] hashLeft;
	static boolean[][] hashRight;
	static boolean[][] hashUp;
	static boolean[][] hashDown;
	static char[][] map;
	public static void main(String[] args){
		Scanner sc=new Scanner(System.in);
		int T=sc.nextInt();
		for (int t=1;t<=T;t++){
			int r=sc.nextInt();
			int c=sc.nextInt();
			sc.nextLine();
			hashLeft=new boolean[r][c];
			hashRight=new boolean[r][c];
			hashUp=new boolean[r][c];
			hashDown=new boolean[r][c];
			map=new char[r][c];
			for (int i=0;i<r;i++){
				String temp=sc.nextLine();						
				for (int j=0;j<c;j++){
					map[i][j]=temp.charAt(j);
				}
			}
			for (int i=0;i<r;i++){
				for (int j=1;j<c;j++){
					if (hashLeft[i][j-1]){
						hashLeft[i][j]=true;
					}else{
						if (map[i][j-1]!='.'){
							hashLeft[i][j]=true;
						}
					}
				}
			}

			for (int i=1;i<r;i++){
				for (int j=0;j<c;j++){
					if (hashUp[i-1][j]){
						hashUp[i][j]=true;
					}else{
						if (map[i-1][j]!='.'){
							hashUp[i][j]=true;
						}
					}
				}
			}

			for (int i=0;i<r;i++){
				for (int j=c-2;j>=0;j--){
					if (hashRight[i][j+1]){
						hashRight[i][j]=true;
					}else{
						if (map[i][j+1]!='.'){
							hashRight[i][j]=true;
						}
					}
				}
			}

			for (int i=r-2;i>=0;i--){
				for (int j=0;j<c;j++){
					if (hashDown[i+1][j]){
						hashDown[i][j]=true;
					}else{
						if (map[i+1][j]!='.'){
							hashDown[i][j]=true;
						}
					}
				}
			}

			int ans=0;

			boolean find=false;
			label:
			for (int i=0;i<r;i++){
				for (int j=0;j<c;j++){
					char cc=map[i][j];
					if (cc=='.'){
						continue;
					}
					if (cc=='>'){
						if (hashRight[i][j]){
							continue;
						}
						if (hashLeft[i][j]||hashUp[i][j]||hashDown[i][j]){
							ans++;
						}else{
							find=true;
							break label;
						}
					}
					if (cc=='<'){
						if (hashLeft[i][j]){
							continue;
						}
						if (hashRight[i][j]||hashUp[i][j]||hashDown[i][j]){
							ans++;
						}else{
							find=true;
							break label;
						}
					}
					if (cc=='v'){
						if (hashDown[i][j]){
							continue;
						}
						if (hashLeft[i][j]||hashUp[i][j]||hashRight[i][j]){
							ans++;
						}else{
							find=true;
							break label;
						}
					}
					if (cc=='^'){
						if (hashUp[i][j]){
							continue;
						}
						if (hashLeft[i][j]||hashDown[i][j]||hashRight[i][j]){
							ans++;
						}else{
							find=true;
							break label;
						}
					}
				}
			}
			if (find){
				System.out.println("Case #"+t+": IMPOSSIBLE");
			}else{
				System.out.println("Case #"+t+": "+ans);
			}
		}
	}
}

package googlejam4.p096;
import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.util.LinkedList;
import java.util.StringTokenizer;


public class Pegman {

	/**
	 * @param args
	 */
	public static void main(String[] args) throws Exception {
		// TODO Auto-generated method stub
		BufferedReader br=new BufferedReader(new InputStreamReader(System.in));
		BufferedWriter bw=new BufferedWriter(new OutputStreamWriter(System.out));
		StringTokenizer st;
		int T=Integer.parseInt(br.readLine());
		for(int cn=1;cn<=T;cn++){
			st=new StringTokenizer(br.readLine());
			int R=Integer.parseInt(st.nextToken());
			int C=Integer.parseInt(st.nextToken());
			char arr[][]=new char[R][C];
			for(int i=0;i<R;i++){
				String str=br.readLine();
				for(int j=0;j<C;j++){
					arr[i][j]=str.charAt(j);
				}
			}
			//count how many point out
			LinkedList<Integer> list=new LinkedList<Integer>();
			for(int i=0;i<R;i++){
				for(int j=0;j<C;j++){
					int dx=0, dy=0;
					if(arr[i][j]=='.')
						continue;
					else if(arr[i][j]=='^'){
						dx=-1;dy=0;
					}
					else if(arr[i][j]=='v'){
						dx=1;dy=0;
					}
					else if(arr[i][j]=='<'){
						dx=0;dy=-1;
					}
					else if(arr[i][j]=='>'){
						dx=0;dy=1;
					}
					int cx=i+dx, cy=j+dy;
					while(cx>=0&&cx<R&&cy>=0&&cy<C){
						if(arr[cx][cy]!='.')
							break;
						cx+=dx;
						cy+=dy;
					}
					if(cx<0||cx>=R||cy<0||cy>=C){
						list.add(i*100+j);
					}
				}
			}
			//find if can change direction
			boolean okay=true;
			for(int li:list){
				int i=li/100, j=li%100;
				int dx[]={-1,1,0,0};
				int dy[]={0,0,-1,1};
				boolean found=false;
				for(int dn=0;dn<4;dn++){
					int cx=i+dx[dn], cy=j+dy[dn];
					while(cx>=0&&cx<R&&cy>=0&&cy<C){
						if(arr[cx][cy]!='.'){
							found=true;
							break;
						}
						cx+=dx[dn];
						cy+=dy[dn];
					}
				}
				if(!found)
					okay=false;
			}

			int ans=list.size();
			if(okay)
				bw.append("Case #"+cn+": "+ans+"\n");
			else
				bw.append("Case #"+cn+": IMPOSSIBLE\n");
		}
		bw.flush();
	}

}

package googlejam4.p076;


import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.File;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;

public class P1
{
	public static void main( String[] args ) throws Exception
	{
		File inputFile = new File( "inputfiles/round2/P1/input.txt" );
		FileReader fr = new FileReader( inputFile );
		BufferedReader br = new BufferedReader( fr );

		int numOfTestCases = Integer.parseInt( br.readLine() );

		File outputFile = new File( "inputfiles/round2/P1/output.txt" );
		outputFile.delete();
		outputFile.createNewFile();
		FileWriter fw = new FileWriter( outputFile );
		BufferedWriter bw = new BufferedWriter( fw );

		for ( int i = 0; i < numOfTestCases; i++ )
		{
			String[] rc = br.readLine().split( " " );
			int R = Integer.parseInt( rc[0] );
			int C = Integer.parseInt( rc[1] );

			char[][] grid = new char[R][C];

			for ( int j = 0; j < R; j++ )
			{
				String row = br.readLine();
				for ( int k = 0; k < row.length(); k++ )
					grid[j][k] = row.charAt( k );
			}

			int changeNum = 0;
			boolean possible = true;

			main: for ( int i1 = 0; i1 < R; i1++ )
				for ( int j = 0; j < C; j++ )
				{
					char field = grid[i1][j];
					if ( field == '.' )
						continue;

					boolean isArrowUp = false;
					for ( int k = 0; k < i1; k++ )
						if ( grid[k][j] != '.' )
						{
							isArrowUp = true;
							break;
						}

					boolean isArrowDown = false;
					for ( int k = i1+1; k < R; k++ )
						if ( grid[k][j] != '.' )
						{
							isArrowDown = true;
							break;
						}

					boolean isArrowLeft = false;
					for ( int k = 0; k < j; k++ )
						if ( grid[i1][k] != '.' )
						{
							isArrowLeft = true;
							break;
						}

					boolean isArrowRight = false;
					for ( int k = j+1; k < C; k++ )
						if ( grid[i1][k] != '.' )
						{
							isArrowRight = true;
							break;
						}

					if ( !isArrowUp && !isArrowDown && !isArrowLeft && !isArrowRight )
					{
						possible = false;
						break main;
					}

					if ( field == '^' && !isArrowUp )
						changeNum++;

					if ( field == 'v' && !isArrowDown )
						changeNum++;

					if ( field == '>' && !isArrowRight )
						changeNum++;

					if ( field == '<' && !isArrowLeft )
						changeNum++;
				}

			String s = "Case #" + (i+1) + ": " + ( possible ? changeNum : "IMPOSSIBLE" ) + "\n";
			bw.append( s );
			bw.flush();
		}

		bw.flush();
		fw.flush();

		bw.close();
		fw.close();
	}
}

package googlejam4.p197;
import java.util.*;

public class A {
static int dx[] = {0, -1, 0, 1, 0};
static int dy[] = {0, 0, 1, 0, -1};
public static void main(String [] args) {
	Scanner scanner = new Scanner(System.in);
	int nCase = scanner.nextInt();

	for (int nc = 1; nc <= nCase; nc++) {
		int R = scanner.nextInt();
		int C = scanner.nextInt();
		scanner.nextLine();

		char[][] map = new char[R][C];

		for (int i = 0; i < R; i++) {
			String in = scanner.nextLine();
			for (int j = 0; j < C; j++)
				switch (in.charAt(j)) {
					case '.':
						map[i][j] = 0;
						break;
					case '^':
						map[i][j] = 1;
						break;
					case '>':
						map[i][j] = 2;
						break;
					case 'v':
						map[i][j] = 3;
						break;
					case '<':
						map[i][j] = 4;
						break;
				}
		}

		int ans = 0;

		for (int i = 0; i < R && ans >= 0; i++)
			for (int j = 0; j < C; j++)
				if (map[i][j] > 0) {
					int R1 = map.length;
					int C1 = map[0].length;	
					int oldDir = map[i][j];
					int ans1 = -1;
					for (int dir = 1; dir <= 4 && ans1 != 0; dir++) {
						int nx = i + dx[dir];
						int ny = j + dy[dir];
						while (0 <= nx && nx < R1 && 0 <= ny && ny < C1) {
							if (map[nx][ny] > 0) {
								if (dir == oldDir) {
									ans1 = 0;
									break;
								} else {
									ans1 = 1;
									break;
								}
							}
							nx = nx + dx[dir];
							ny = ny + dy[dir];
						}
						//System.out.printf("x:%d y:%d oldDir:%d dir:%d ans:%d", x, y, oldDir, dir, ans);
					}
					int need = ans1;
					if (need < 0) {
						ans = -1;
						break;
					} else
						ans += need;
				}

		if (ans >= 0)
			System.out.println("Case #" + nc + ": " + ans);
		else
			System.out.println("Case #" + nc + ": IMPOSSIBLE");
	}
}
}

