					if (fleches[i][j]=='v'){
						for (int k = i+1; k<R; k++){
							if (fleches[k][j]!='.'){
								needsToChange=false;
								break;
							}
						}
					}
					if (needsToChange){

						boolean changeable = false;
						for (int k = i+1; k<R; k++){
							if (fleches[k][j]!='.'){
								changeable=true;
								break;
							}
						}
						for (int k = i-1; k>=0; k--){
							if (fleches[k][j]!='.'){
								changeable=true;
								break;
							}
						}
						for (int k = j+1; k<C; k++){
							if (fleches[i][k]!='.'){
								changeable=true;
								break;
							}
						}
						for (int k = j-1; k>=0; k--){
							if (fleches[i][k]!='.'){
								changeable=true;
								break;
							}
						}
						if (changeable){
							result++;
						}
						else{
							result=Integer.MIN_VALUE;
							break;
						}
					}
				}
			}
			if (result<0){

				fw.write("Case #"+(numCase+1)+": IMPOSSIBLE\n");
			}
			else{

			fw.write("Case #"+(numCase+1)+": "+result+"\n");
			}

		}
		fw.close();
		System.out.println("finished");
	}

}

package googlejam4.p144;
import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.io.PrintWriter;
import java.util.StringTokenizer;


public class A {

	static StringTokenizer st;
	static BufferedReader br;
	static PrintWriter pw;
	static boolean[][]used;

	public static void main(String[] args) throws IOException{
		br = new BufferedReader(new InputStreamReader(System.in));
		pw = new PrintWriter(new BufferedWriter(new OutputStreamWriter(System.out)));
		int test = Integer.parseInt(next());
		for (int t = 1; t <= test; t++) {
			System.out.print("Case #"+t+": ");
			int n = Integer.parseInt(next());
			int m = Integer.parseInt(next());
			char[][]a = new char[n+1][m+1];
			for (int i = 1; i <= n; i++) {
				String s = next();
				for (int j = 1; j <= m; j++) {
					a[i][j] = s.charAt(j-1);
				}
			}
			int ans = 0;
			for (int i = 1; i <= n; i++) {
				for (int j = 1; j <= m; j++) {
					if (a[i][j] != '.') {
						boolean found = false;
						int add = 1;
						for (int k = i+1; k <= n; k++) {
							if (a[k][j] != '.') {
								if (a[i][j]=='v')
									add = 0;
								found = true;
							}
						}
						for (int k = i-1; k >= 1; k--) {
							if (a[k][j] != '.') {
								if (a[i][j]=='^')
									add = 0;
								found = true;
							}
						}
						for (int k = j+1; k <= m; k++) {
							if (a[i][k] != '.') {
								if (a[i][j]=='>')
									add = 0;
								found = true;
							}
						}
						for (int k = j-1; k >= 1; k--) {
							if (a[i][k] != '.') {
								if (a[i][j]=='<')
									add = 0;
								found = true;
							}
						}
						if (!found) {
							ans = -1;
							break;
						}
						ans += add;
					}
				}
			}
			if (ans==-1)
				System.out.println("IMPOSSIBLE");
			else {
				System.out.println(ans);
			}
		}
		pw.close();
	}

	private static String next() throws IOException{
		while (st==null || !st.hasMoreTokens())
			st = new StringTokenizer(br.readLine());
		return st.nextToken();
	}

}

package googlejam4.p186;
import java.util.*;
import java.io.*;

public class A
{
	public static void main(String[] args) throws IOException
	{
		BufferedReader in=new BufferedReader(new InputStreamReader(System.in));

		int numRuns=Integer.parseInt(in.readLine());
		for (int run=1; run<=numRuns; run++)
		{
			StringTokenizer tok=new StringTokenizer(in.readLine());
			int r=Integer.parseInt(tok.nextToken()), c=Integer.parseInt(tok.nextToken());
			int[][] dir=new int[r][c];
			int[][] row=new int[r][c];
			int[][] col=new int[c][r];
			int[] inRow=new int[r];
			int[] inCol=new int[c];

			for (int i=0; i<r; i++)
			{
				String line=in.readLine();
				for (int j=0; j<c; j++)
				{
					if (line.charAt(j)!='.')
					{
						row[i][inRow[i]]=j;
						inRow[i]++;
						col[j][inCol[j]]=i;
						inCol[j]++;
					}
					if (line.charAt(j)=='<')
						dir[i][j]=0;
					else if (line.charAt(j)=='^')
						dir[i][j]=1;
					else if (line.charAt(j)=='>')
						dir[i][j]=2;
					else if (line.charAt(j)=='v')
						dir[i][j]=3;
				}
			}

			int changes=0;
			boolean[] okay={false, false, false, false};
			int colPos;
			for (int i=0; i<r && changes>=0; i++)
				for (int j=0; j<inRow[i] && changes>=0; j++)
				{
					colPos=Arrays.binarySearch(col[row[i][j]], 0, inCol[row[i][j]], i);
					okay[0]=j>0;
					okay[1]=colPos>0;
					okay[2]=j<inRow[i]-1;
					okay[3]=colPos<inCol[row[i][j]]-1;

					if (okay[dir[i][row[i][j]]])
						continue;
					else if (okay[0] || okay[1] || okay[2] || okay[3])
						changes++;
					else
						changes=-1;
				}
			System.out.print("Case #"+run+": ");
			if (changes==-1)
				System.out.println("IMPOSSIBLE");
			else
				System.out.println(changes);
		}
	}
}

