			for (int r = 0; r < R; r++) {
				for (int c = 0; c < C; c++) {
					if (board[r][c] != '.') {
						if (rowArrows[r] == 1 && colArrows[c] == 1) {
							imp = true;
							break;
						}
					}
				}
				if (imp) {
					break;
				}
			}

			if (imp) {
				System.out.format("Case #%d: IMPOSSIBLE\n", t);
			} else {
				System.out.format("Case #%d: %d\n", t, res);
			}

		}

		in.close();
	}

}

package googlejam4.p136;
import java.util.Scanner;


public class A {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int nT = sc.nextInt();
		for(int T=1; T<=nT; ++T)
		{
			int R=sc.nextInt(), C=sc.nextInt();

			int[][] map = new int[R][C];
			char[] line = new char[C];

			int[] dr = {0,-1, 0, 1, 0};
			int[] dc = {0, 0, 1, 0,-1};

			for(int r=0; r<R; ++r)
			{
				line = sc.next().toCharArray();
				for(int c=0; c<C; ++c)
				{
					switch(line[c])
					{
					case '.': map[r][c]=0; break;
					case '^': map[r][c]=1; break;
					case '>': map[r][c]=2; break;
					case 'v': map[r][c]=3; break;
					case '<': map[r][c]=4; break;
					}
				}
			}


			int total=0;
			boolean possible=true;
			for(int r=0; r<R && possible; ++r)
			{
				for(int c=0; c<C && possible; ++c)
				{
					if(map[r][c]==0)
						continue;
					int d=1;
					int r1 = r+d*dr[map[r][c]];
					int c1 = c+d*dc[map[r][c]];
					while((0<=r1 && r1<R && 0<=c1 && c1<C) && map[r+d*dr[map[r][c]]][c+d*dc[map[r][c]]]==0)
						++d;
					int r2 = r+d*dr[map[r][c]];
					int c2 = c+d*dc[map[r][c]];
					if(!(0<=r2 && r2<R && 0<=c2 && c2<C)) //fell off
					{
						boolean found = false;
						for(int del=1; del<=4; ++del)
						{
							d=1;
							int r3 = r+d*dr[del];
							int c3 = c+d*dc[del];
							while((0<=r3 && r3<R && 0<=c3 && c3<C) && map[r+d*dr[del]][c+d*dc[del]]==0)
								++d;
							int r4 = r+d*dr[del];
							int c4 = c+d*dc[del];
							if((0<=r4 && r4<R && 0<=c4 && c4<C)) //found arrow
							{
								found = true;
								break;
							}
						}
						if(!found)
							possible=false;
						else
							++total;
					}
				}
			}

			if(possible)
				System.out.println("Case #"+T+": "+total);
			else
				System.out.println("Case #"+T+": IMPOSSIBLE");
		}

	}
}

package googlejam4.p007;
import static java.lang.Math.*;
import static java.util.Arrays.*;

import java.io.*;
import java.util.*;

public class A {

	PrintWriter out;

	int R, C;
	char[][] cs;

	char[] D = {'^', '<', 'v', '>'};
	int[] dx = {1, 0, -1, 0}, dy = {0, 1, 0, -1};

	public static int __ID = 0;
	public static boolean __ERROR = false;

	public static void main(String[] args) {
		int pN = 1;
		if (args.length == 0) {
			try {
				System.setIn(new BufferedInputStream(new FileInputStream(A.class.getName() + ".in")));
			} catch (Exception e) {
			}
		} else {
			pN = Integer.parseInt(args[0]);
		}
		PrintStream out = System.out;
		System.setOut(null);
		Scanner sc = new Scanner(System.in);
		final int caseN = sc.nextInt();
		final A[] solvers = new A[caseN];
		StringWriter[] outs = new StringWriter[caseN];
		for (int i = 0; i < caseN; i++) {
			solvers[i] = new A();
			outs[i] = new StringWriter();
			solvers[i].out = new PrintWriter(outs[i]);
			solvers[i].out.printf("Case #%d: ", i + 1);
			A r = solvers[i];
			r.R = sc.nextInt();
			r.C = sc.nextInt();
			r.cs = new char[r.R][];
			for (int i1 = 0; i1 < r.R; i1++) r.cs[i1] = sc.next().toCharArray();
		}
		Thread[] ts = new Thread[pN];
		for (int i = 0; i < pN; i++) {
			ts[i] = new Thread() {
				@Override
				public void run() {
					for (;;) {
						int id;
						synchronized (A.class) {
							if (__ID == caseN) return;
							id = __ID++;
						}
						try {
							A r = solvers[id];
							boolean[][][] dame = new boolean[r.R][r.C][4];
							for (int d = 0; d < 4; d++) {
								for (int i = 0; i < r.R; i++) {
									for (int j = 0; j < r.C; j++) {
										int i1 = i - r.dx[d];
										int i2 = j - r.dy[d];
										if ((i == 0 || i == r.R - 1 || j == 0 || j == r.C - 1) && (!(0 <= i1 && i1 <= r.R - 1) || !(0 <= i2 && i2 <= r.C - 1))) {
											int x = i, y = j;
											while (0 <= x && x <= r.R - 1 && 0 <= y && y <= r.C - 1) {
												if (r.cs[x][y] != '.') {
													dame[x][y][d] = true;
													break;
												}
												x += r.dx[d];
												y += r.dy[d];
											}
										}
									}
								}
							}
							int res = 0;
							boolean impossible = false;
							for (int i = 0; i < r.R; i++) {
								for (int j = 0; j < r.C; j++) if (r.cs[i][j] != '.') {
									boolean ok = true, can = false;
									for (int k = 0; k < 4; k++) {
										if (r.cs[i][j] == r.D[k] && dame[i][j][k]) ok = false;
										if (!dame[i][j][k]) can = true;
									}
									if (!ok) res++;
									if (!can) impossible = true;
								}
							}
							if (impossible) r.out.println("IMPOSSIBLE");
							else r.out.println(res);
						} catch (RuntimeException e) {
							__ERROR = true;
							System.err.printf("Error in case %d:%n", id + 1);
							e.printStackTrace();
						}
						solvers[id].out.flush();
						solvers[id] = null;
					}
				}
			};
			ts[i].start();
		}
		for (int i = 0; i < pN; i++) {
			try {
				ts[i].join();
			} catch (InterruptedException e) {
				i--;
				continue;
			}
		}
		for (int i = 0; i < caseN; i++) {
			out.print(outs[i].toString());
		}
		if (__ERROR) out.printf("%nError occured!!!%n");
	}

}

package googlejam4.p168;


import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.FileReader;
import java.io.FileWriter;
import java.util.StringTokenizer;

public class Pegman {
	public static void main(String[] args) throws Exception {
		String fileName = args[0];
		Pegman obj = new Pegman();
		BufferedReader br = new BufferedReader(new FileReader(fileName));
		BufferedWriter bw = new BufferedWriter(
				new FileWriter(fileName + ".out"));

		int T = Integer.parseInt(br.readLine());
		for (int i = 0; i < T; i++) {
			String str = br.readLine();
			StringTokenizer token = new StringTokenizer(str, " ");
			int R = Integer.parseInt(token.nextToken());
			int C = Integer.parseInt(token.nextToken());
			int[][] map = new int[C][R];
			for (int j = 0 ; j < R ; j++) {
				str = br.readLine();
				for (int k = 0 ; k < C ; k++) {
					switch (str.charAt(k)) {
					case '^':
						map[k][j] = 1;
						break;
					case '>':
						map[k][j] = 2;
						break;
					case 'v':
						map[k][j] = 3;
						break;
					case '<':
						map[k][j] = 4;
						break;
					default:
						map[k][j] = 0;
						break;
					}
				}
			}
			int ans = 0;
			for (int j = 0 ; j < R; j++) {
				for (int k = 0 ; k < C ; k++) {
					if (map[k][j] == 0) {
						continue;
					}
					int x = k;
					int y = j;
					boolean flg = false;
					while (true) {
						x += obj.DIR[map[k][j]][0];
						y += obj.DIR[map[k][j]][1];
						if ( x < 0 || x >= C || y < 0 || y >= R ) {
							break;
						}
						if (map[x][y] != 0) {
							flg = true;
							break;
						}
					}
					if (flg) {
						continue;
					}
					flg = false;
					for (int d = 1 ; d <= 4 ; d++ ) {
						if (map[k][j] == d) {
							continue;
						}
						x = k;
						y = j;
						while (true) {
							x += obj.DIR[d][0];
							y += obj.DIR[d][1];
							if ( x < 0 || x >= C || y < 0 || y >= R ) {
								break;
							}
							if (map[x][y] != 0) {
								flg = true;
								break;
							}
						}
						if (flg) {
							break;
						}
					}
					if (flg) {
						ans += 1;
						continue;
					} else {
						ans = -1;
						break;
					}
				}
				if (ans == -1) {
					break;
				}
			}

			if (ans == -1) {
				bw.write("Case #" + (i + 1) + ": " + "IMPOSSIBLE");
			} else {
				bw.write("Case #" + (i + 1) + ": " + ans);
			}
			bw.write("\r\n");
		}
		bw.close();
		br.close();
	}

	final int[][] DIR = {{0,0},{0,-1},{1,0},{0,1},{-1,0}};
}

