import java.io.*;
import java.math.*;
import java.util.*;

public class A
{
	public static void main(String[] args) throws IOException
	{
		BufferedReader in  = new BufferedReader(new FileReader("A.in"));
		BufferedWriter out = new BufferedWriter(new FileWriter("A.out"));

		int T = Integer.parseInt(in.readLine());
		for(int t=1;t<=T;t++)
		{
			StringTokenizer st = new StringTokenizer(in.readLine());
			int N = Integer.parseInt(st.nextToken());
			int R = Integer.parseInt(st.nextToken());
			int P = Integer.parseInt(st.nextToken());
			int S = Integer.parseInt(st.nextToken());

			String ans = "IMPOSSIBLE";

			int[][] win = {{0, 1}, {1, 2}, {0, 2}};
			for(int i=0;i<3;i++)
			{
				String store = new String[]{"P", "R", "S"}[i];
				for(int k=0;k<N;k++)
				{
					String temp = "";
					for(char c : store.toCharArray())
						if(c == 'P')
							temp += "PR";
						else if(c == 'R')
							temp += "RS";
						else
							temp += "PS";

					store = temp;
				}

				int p = 0, r = 0, s = 0;
				for(char c : store.toCharArray())
					if(c == 'P')
						p++;
					else if(c == 'R')
						r++;
					else
						s++;

				for(int j=1;j<(1 << N);j *= 2)
					for(int k=0;k+j<(1 << N);k+=j+j)
						if(store.substring(k+j, k+2*j).compareTo(store.substring(k, k+j)) < 0)
							store = store.substring(0, k) + store.substring(k+j, k+2*j) + store.substring(k, k+j) + store.substring(k+2*j);

				if(P == p && R == r && S == s && (ans.equals("IMPOSSIBLE") || store.compareTo(ans) < 0))
					ans = store;
			}

			out.write("Case #"+t+": ");
			out.write(ans);
			out.write("\n");
		}

		in .close();
		out.close();
	}
}

package googlejam10.p181;
import java.io.*;
import java.util.*;

public class A {
    public static void main(String[]args) throws IOException {
        BufferedReader x = new BufferedReader(new InputStreamReader(System.in));
        int t = Integer.parseInt(x.readLine());
        for (int cases = 1; cases <= t; cases++) {
            StringTokenizer st = new StringTokenizer(x.readLine());
            int n = Integer.parseInt(st.nextToken());
            int r = Integer.parseInt(st.nextToken());
            int p = Integer.parseInt(st.nextToken());
            int s = Integer.parseInt(st.nextToken());
			int num1 = 1;
			for (int i1 = 0; i1 < n; i1++) {
			    num1 *= 2;
			}
            //check possibility
            int num = num1;
            if (n % 2 == 1) num++;
            num /= 3;
            if ((r == num && s == num) || (s == num && p == num) || (r == num && p == num)) {
                if (n == 1) {
                    if (r == 0) System.out.println("Case #" + cases + ": PS");
                    if (s == 0) System.out.println("Case #" + cases + ": PR");
                    if (p == 0) System.out.println("Case #" + cases + ": RS");
                    continue;
                }
                String ans = "";
                if (n % 2 == 0) {
                    if (p != num) ans = "S";
                    if (r != num) ans = "P";
                    if (s != num) ans = "R";
                } else {
                    if (p != num) ans = "P";
                    if (r != num) ans = "R";
                    if (s != num) ans = "S";
                }
                for (int i = n - 1; i >= 0; i--) {
                    String temp = "";
                    for (int j = 0; j < ans.length(); j++) {
                        if (ans.charAt(j) == 'S') {
                            if (i % 6 == 0 || i % 6 == 1 || i % 6 == 5) {
                                temp += "PS";
                            } else {
                                temp += "SP";
                            }
                        }
                        if (ans.charAt(j) == 'P') {
                            if (i % 6 == 0 || i % 6 == 1 || i % 6 == 2) {
                                temp += "PR";
                            } else {
                                temp += "RP";
                            }
                        }
                        if (ans.charAt(j) == 'R') {
                            if (i % 6 == 0 || i % 6 == 4 || i % 6 == 5) {
                                temp += "RS";
                            } else {
                                temp += "SR";
                            }
                        }
                    }
                    ans = temp;
                }
                System.out.println("Case #" + cases + ": " + ans);
            } else {
                System.out.println("Case #" + cases + ": IMPOSSIBLE");
            }
        }
    }
}

package googlejam10.p192;
import java.util.*;

public class A {

	public static void main(String[] args) {

		Scanner in = new Scanner(System.in);

		int num = in.nextInt();
		for(int i = 0; i < num; i++) {
			boolean ayy = false;
			System.out.print("Case #" + (i+1) + ": ");
			int n = in.nextInt();
			int nn = (int) Math.pow(2, n);
			int r = in.nextInt();
			int p = in.nextInt();
			int s = in.nextInt();
			char[] test = new char[nn];
			Arrays.fill(test, 'p');
			if(p == nn) {
				System.out.print("IMPOSSIBLE");
				ayy = true;
			} else {
				boolean solvable = true;
				boolean unsolved = true;
				while(solvable) {
					for(int ii = nn-1; ii >= 0; ii--) {
						if(test[ii] == 'p') {
							test[ii] = 'r';
							break;
						} else if (test[ii] == 'r') {
							test[ii] = 's';
							break;
						}
						if(ii == 0) {
							solvable = false;
							s = -1;
							break;
						} else {
							test[ii] = 'p';
						}
					}
					int cr = 0;
					int cp = 0;
					int cs = 0;
					/*for(int ff = 0; ff < nn; ff++) {
						System.out.print(test[ff]);
					}
					System.out.println();*/
					for(int j = 0; j < nn; j++) {
						if(test[j] == 'p') cp++;
						if(test[j] == 'r') cr++;
						if(test[j] == 's') cs++;
					}
					if(cr != r || cp != p || cs != s) {
							continue;
						}
					//System.out.println("GOOD");
					boolean decent = true;
					while(true && unsolved && decent) {
						int nnn = nn;
						char[] prev = new char[nn];
						char[] rec;
						for(int sp = 0; sp < nn; sp++) {
							prev[sp] = test[sp];
						}
						while(nnn > 1) {
							nnn /= 2;
							rec = new char[nnn];
							for(int mm = 0; mm < nnn; mm++) {
								if(prev[mm*2] == prev[mm*2+1]) {
									nnn = 0;
									decent = false;
									break;
								}
								if(prev[mm*2] != 'r' && prev[mm*2+1] != 'r') {
									rec[mm] = 's';
								} else if(prev[mm*2] != 's' && prev[mm*2+1] != 's') {
									rec[mm] = 'p';
								} else {
									rec[mm] = 'r';
								}
							}
							prev = rec;
						}
						if(decent) {
							unsolved = false;
						}
					}
					if(!unsolved) {
						for(int meh = 0; meh < nn; meh++) {
							if(test[meh] == 'r') System.out.print('R');
							else if(test[meh] == 's') System.out.print('S');
							else System.out.print('P');
						}
						ayy = true;
						break;
					}
				}
			}
			if(!ayy) System.out.print("IMPOSSIBLE");
			System.out.println();
		}
	}

}

