                        res.add(new String(prev));
                    }
                    bw.write("Case #" + test + ": ");
                    if (res.isEmpty())
                        bw.write("IMPOSSIBLE");
                    else {
                        String result = null;
                        for (String s: res) {
                            if (result == null)
                                result = s;
                            else {
                                if (result.compareTo(s) > 0)
                                    result = s;
                            }
                        }
                        bw.write(result);
                    }
                    bw.write("\n");
                }
                bw.close();
            }

        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

