public class A {
    static String[] r = new String[13];
    static String[] p = new String[13];
    static String[] s = new String[13];

    public static void main(String[] args) {
        r[0] = "R";
        p[0] = "P";
        s[0] = "S";
        for (int i = 0; i < 12; i++) {
            if (r[i].compareTo(p[i]) < 0) {
                p[i + 1] = r[i] + p[i];
            } else {
                p[i + 1] = p[i] + r[i];
            }

            if (r[i].compareTo(s[i]) < 0) {
                r[i + 1] = r[i] + s[i];
            } else {
                r[i + 1] = s[i] + r[i];
            }

            if (s[i].compareTo(p[i]) < 0) {
                s[i + 1] = s[i] + p[i];
            } else {
                s[i + 1] = p[i] + s[i];
            }
        }

        Scanner sc = new Scanner(System.in);
        int numCases = sc.nextInt();
        for (int caseNum = 1; caseNum <= numCases; caseNum++) {
            int N = sc.nextInt();

            int[] R = new int[N + 1];
            R[0] = sc.nextInt();
            int[] P = new int[N + 1];
            P[0] = sc.nextInt();
            int[] S = new int[N + 1];
            S[0] = sc.nextInt();

            for (int i = 0; i < N; i++) {
                P[i + 1] = (P[i] + R[i] - S[i]) / 2;
                R[i + 1] = (R[i] + S[i] - P[i]) / 2;
                S[i + 1] = (P[i] + S[i] - R[i]) / 2;
            }

            System.out.print("Case #" + caseNum + ": ");
            boolean possible = true;
            for (int i = 0; i <= N; i++) {
                if (R[i] < 0 || P[i] < 0 || S[i] < 0) {
                    System.out.println("IMPOSSIBLE");
                    possible = false;
                    break;
                }
            }
            if (!possible) {
                continue;
            }
            if (R[N] == 1) {
                System.out.println(r[N]);
            } else if (P[N] == 1) {
                System.out.println(p[N]);
            } else {
                System.out.println(s[N]);
            }
        }
        sc.close();
    }
}

package googlejam10.p106;
import java.io.*;
import java.util.*;
import java.math.*;

public class ProblemA {

	public static void main(String[] args) throws NumberFormatException, IOException {
		Scanner in = new Scanner(new BufferedReader(new InputStreamReader(System.in)));
		int numCases = Integer.parseInt(in.nextLine());
		for (int caseNum = 1; caseNum <= numCases; caseNum++) {
			String line = in.nextLine();
			Scanner sc = new Scanner(line);
			int n = Integer.parseInt(sc.next());
			int r = Integer.parseInt(sc.next());
			int p = Integer.parseInt(sc.next());
			int s = Integer.parseInt(sc.next());
			int[] test = new int[3];
			test[0] = 1;
			test[1] = 1;
			for(int i = 1; i < n; i++){
				int[] nextStep = new int[3];
				nextStep[0] = test[0] + test[2];
				nextStep[1] = test[1] + test[0];
				nextStep[2] = test[1] + test[2];
				test[0] = nextStep[0];
				test[1] = nextStep[1];
				test[2] = nextStep[2];
			}
			ArrayList<String> round = new ArrayList<String>();

			if(test[0] == p && test[1] == r && test[2] == s){
				round.add("PR");
			} else if(test[0] == r && test[1] == s && test[2] == p){
				round.add("RS");
			} else if(test[0] == s && test[1] == p && test[2] == r){
				round.add("PS");
			} else {
				System.out.println("Case #" + caseNum + ": " + "IMPOSSIBLE");
				continue;
			}
			for(int i = 1; i < n; i++){
				ArrayList<String> nextRound = new ArrayList<String>();
				for(int j = 0; j < round.size(); j++){
					if(round.get(j).equals("PR")){
						nextRound.add("PR");
						nextRound.add("RS");
					} else if(round.get(j).equals("PS")){
						nextRound.add("PR");
						nextRound.add("PS");
					} else if(round.get(j).equals("RS")){
						nextRound.add("RS");
						nextRound.add("PS");
					}
				}
				round = nextRound;
			}
			for(int i = 1; i < n; i++){
				ArrayList<String> lastRound = new ArrayList<String>();
				for(int j = 0; j < round.size()/2; j++){
					String s1 = round.get(2*j);
					String s2 = round.get(2*j + 1);
					if(s1.compareTo(s2) < 0){
						lastRound.add(s1 + s2);
					} else {
						lastRound.add(s2 + s1);
					}
				}
				round = lastRound;
			}
			if(round.size() != 1){
				System.out.println("FAIL");
			}
			System.out.println("Case #" + caseNum + ": " + round.get(0));
		}
	}
}

package googlejam10.p067;


import java.io.File;
import java.io.PrintWriter;
import java.util.Scanner;

public class RatherPerplexingShowdown {

    public static void main(String[] args) throws Exception {
        File inputFile = new File("A-large.in");
        Scanner in = new Scanner(inputFile);
        File outputFile = new File("output.txt");
        PrintWriter out = new PrintWriter(outputFile);

        String[][] answers = new String[13][3];
        answers[0] = new String[] {"P", "R", "S"};
        for (int n=1; n<=12; n++) {
            // P <- P R
            String s1 = answers[n-1][0] + answers[n-1][1];
            String s2 = answers[n-1][1] + answers[n-1][0];
            answers[n][0] = s1.compareTo(s2) < 0 ? s1 : s2;
            // R <- R S
            s1 = answers[n-1][1] + answers[n-1][2];
            s2 = answers[n-1][2] + answers[n-1][1];
            answers[n][1] = s1.compareTo(s2) < 0 ? s1 : s2;
            // S <- S P
            s1 = answers[n-1][2] + answers[n-1][0];
            s2 = answers[n-1][0] + answers[n-1][2];
            answers[n][2] = s1.compareTo(s2) < 0 ? s1 : s2;            
        }

        int T = in.nextInt();
        for (int t=0; t<T; t++) {
            int N = in.nextInt();
            int R = in.nextInt();
            int P = in.nextInt();
            int S = in.nextInt();
            String best = null;
            for (int i=0; i<3; i++) {
                String cand = answers[N][i];
                int[] stats = new int[3];
                for (char c : cand.toCharArray()) {
                    if (c == 'R') {
                        stats[0]++;
                    } else if (c == 'P') {
                        stats[1]++;
                    } else {
                        stats[2]++;
                    }
                }
                if (stats[0] == R && stats[1] == P && stats[2] == S) {
                    if (best == null || cand.compareTo(best) < 0) {
                        best = cand;
                    }
                }
            }
            String answer = (best == null) ? "IMPOSSIBLE" : best;
            out.println("Case #"+(t+1)+": "+answer);
        }

        out.close();
    }

}

