					int cdiff = length - cStr.length();
					for(int j=0;j<cdiff;j++){
						cStr = "0" + cStr;
					}
				}
				boolean cmatch = true;
				for(int i2=0;i2<length;i2++){
					if(cScore.charAt(i2) != '?' && cScore.charAt(i2) != cStr.charAt(i2)){
						cmatch = false;
						break;
					}
				}
				if(!cmatch){
					continue;
				}
				//cValue is matched

				for(int jValue = 0; jValue<=maxValue;jValue++){
					String jStr = String.valueOf(jValue);
