		try {
			scanner = new Scanner(new FileReader(filename));
			printer = new PrintWriter(new FileWriter(output));

			int tests = scanner.nextInt();
			for (int test = 1; test <= tests; test++) {

				// Input
				int N = scanner.nextInt();
