//				second = second - last;
//			}

			if(first < 0 || second < 0) {
				System.err.println("Case #"+ (caseId + 1));
			}

			System.out.println("Case #"+ (caseId + 1) + ": "+ first + " " + second);
			reader.nextLine();
		}




	}


}

