			for(int j=0; j<n; ++j)
				pList[j]	= scanIn.nextInt();
			int[]	ret	= new int[n-1];

			for(int i1=0; i1<n-1; ++i1)
				ret[i1]	= pList[i1+1] - pList[i1];

			int[]	tmp	= ret;
