		    int[] a = new int[n];
		    for (int i = 0; i < n; i++)
		        a[i] = in.nextInt();
		    int cnt1 = 0;
		    for (int i = 0; i < n - 1; i++)
		        if (a[i + 1] < a[i])
		            cnt1 += a[i] - a[i + 1];

		    int max = 0;
		    for (int i = 0; i < n - 1; i++)
		        if (a[i] > a[i + 1])
		            max = Math.max(max, a[i] - a[i + 1]);
		    int cnt2 = 0;
		    for (int i = 0; i < n - 1; i++)
		        cnt2 += Math.min(max, a[i]);
		    //System.out.println("Case #" + test+ ": " + cnt1 + " " + cnt2);
		    out.println("Case #" + test+ ": " + cnt1 + " " + cnt2);
		}

		out.close();
    }
}

