public class ProblemA {

	private static String inputFilePath = "/home/chiwang/Downloads/CodeJam/A-large.in";
	private static String outputFilePath = "/home/chiwang/Downloads/CodeJam/Solution/";
	private static BufferedReader br;
	private static BufferedWriter bw;

	private static int[] plate;
	private static int N;

	public static void main(String[] args) {

		// Initialisation
		try {
			outputFilePath += inputFilePath.substring(inputFilePath.lastIndexOf("/") + 1, inputFilePath.length() - 3) + ".out";

			br = new BufferedReader(new FileReader(inputFilePath));
			bw = new BufferedWriter(new FileWriter(outputFilePath));

			final int T = Integer.parseInt(br.readLine());

			for (int caseNum = 1; caseNum <= T; caseNum++) {		
				/*
				 * Fill in the implementation details
				 */
				int N = Integer.parseInt(br.readLine());
				plate = new int[N];
				String[] parts = br.readLine().split(" ");

				for (int i = 0; i < N; i++) {
					plate[i] = Integer.parseInt(parts[i]);
				}
				long eaten = 0;
				int prev = plate[0];
				for (int i = 1; i < plate.length; i++) {
					if (prev > plate[i]) {
						eaten += (prev - plate[i]);
					}
					prev = plate[i];
				}

				long m1 = eaten;
				long eaten1 = 0;

				int biggestDiff = 0;
				int prev1 = plate[0];
				for (int i = 1; i < plate.length; i++) {
					if (prev1 - plate[i] > biggestDiff) {
						biggestDiff = prev1 - plate[i];
					}
					prev1 = plate[i];
				}



				for (int i = 0; i < plate.length -1; i++) {
					if (plate[i] > 0) {
						eaten1 += Math.min(biggestDiff, plate[i]);
					}
				}
				long m2 = eaten1;

				String result = String.format("Case #%d: %d %d%n", caseNum, m1 , m2);
				bw.write(result);
				System.out.println(result);
			}		

		} catch (IOException ioe) {
			ioe.printStackTrace();
		} finally {
			try {
				if (br != null)
					br.close();
				if (bw != null)
					bw.close();
			} catch (IOException ioe) {
				ioe.printStackTrace();
			}
		}

		System.out.println("========================================");
		System.out.println("Done! :)");
	}

}

package googlejam1.p428;


import java.io.BufferedReader;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.StringTokenizer;

public class Mushroom {
	public static void main(String[] args) throws IOException {
		BufferedReader f = new BufferedReader(new FileReader("mushroom.in"));
		PrintWriter out = new PrintWriter(new FileWriter("C:/Users/David/Desktop/mushroom.txt"));
		StringTokenizer t = new StringTokenizer(f.readLine());
		int testCases = Integer.parseInt(t.nextToken());
		for (int i = 1; i <= testCases; i++)
		{
			t = new StringTokenizer(f.readLine());
			int N = Integer.parseInt(t.nextToken());
			int[] M = new int[N];
			t = new StringTokenizer(f.readLine());
			for (int j = 0; j < N; j++)
				M[j] = Integer.parseInt(t.nextToken());

			//Method 1
			int method1 = 0;
			for (int j = 1; j < N; j++)
				if (M[j] < M[j-1]) method1 += M[j-1] - M[j];

			//method 2
			int delta_X_Per_Ten = 0;
			for (int j = 1; j < N; j++)
				if (M[j] < M[j-1] && M[j-1] - M[j] > delta_X_Per_Ten)
					delta_X_Per_Ten=M[j-1] - M[j];

			int method2 = 0;
			for (int j = 0; j < N - 1; j++)
				method2 += Math.min(delta_X_Per_Ten, M[j]);

			String ans = "Case #" + i + ": " + method1 + " " + method2;
			out.println(ans);
		}

		out.close(); f.close(); System.exit(0);
	}

}

