				max = Math.max(subt[i], max);
			}

			int ans1 = 0;
			for( int i=0; i<N-1;i++){
				if( subt[i] > 0){
					ans1 += subt[i];
				}
			}

			int ans2 = 0;
			for( int i=0; i<N-1;i++){
				if( num[i] < max){
					ans2 += num[i];
				}
				else{
					ans2 += max;
				}
			}


			System.out.println("Case #"+(t+1)+": "+ans1+" "+ans2);			

		}
	}
}

