				list.add(sc.nextInt());
			}

			long normal = 0;
			long special = 0;
			//normal
			int pred = list.get(0);
			for (int i = 1; i < list.size(); i++) {
				if (pred > list.get(i)) {
					normal += pred - list.get(i);
				}
				pred = list.get(i);
			}

