			String[] ss = in.nextLine().split(" ");
			for (int j = 0; j < n; j++) {
				arr[j] = Integer.parseInt(ss[j]);
			}
			int sum = 0;
			for (int i1 = 0; i1 < arr.length - 1; i1++) {
				if (arr[i1] > arr[i1 + 1])
					sum += arr[i1] - arr[i1 + 1];
			}
			int res1 = sum;
			int sum1 = 0;
			int rate = 0;
			for (int i2 = 0; i2 < arr.length - 1; i2++) {
				if (arr[i2] - arr[i2 + 1] > rate)
					rate = arr[i2] - arr[i2 + 1];
			}

			for (int i1 = 0; i1 < arr.length - 1; i1++) {
				if (arr[i1] >= rate) {
					sum1 += rate;
					arr[i1] -= rate;
				} else { // a[i] < rate
					sum1 += arr[i1];
					arr[i1] = 0;
				}
			}
			int res2 = sum1;
			String s = "Case #" + i + ": " + res1 + " " + res2;
