		    int m1 = 0;
		    for(int y = 0; y < m.length; y++) {
		        if(y != 0) {
		            maxRate = Math.max(maxRate, m[y-1] - m[y]);
		        }
		    }

		    for(int y = 0; y < m.length; y++) {
		        if(y != 0) {
		            int diff = m[y-1] - m[y];
		            if(m[y-1] > 0) {
		                if(m[y-1] >= maxRate) {
		                    m2 += maxRate;
		                } else if(m[y-1] < maxRate) {
		                    m2 += m[y-1];
		                }
		            }

		            if(diff >= 0) {
		                m1 += diff;
		            }
		        }
		    }

		    answer = "" + m1 + " " + m2;

		    out.printf("Case #%d: ", x + 1);
		    out.print(answer);
		    out.println();
		}
//        b();
