		    outputWriter.close();
    }



    // **************** PRINT METHODS ****************



    // **************** INPUT DECLARATION ****************



    // **************** INPUT READING ****************

    static String TOKEN() throws IOException {
        while (st == null || !st.hasMoreTokens())
            st = new StringTokenizer(isStandardInput ? stdin.readLine() : in.readLine());
        return st.nextToken();
    }
}

