	    if (r.mushroom[i] < r.mushroom[i - 1])
	    {
	      r.maxVal = Math.max(r.maxVal, r.mushroom[i - 1] - r.mushroom[i ]);
	    }
	  }
	  r.ans[1] = 0;
	  for (int i = 1; i < r.n; ++i)
	  {
	    r.ans[1] += Math.min(r.mushroom[i - 1], r.maxVal);
	  }
	  System.out.println(r.ans[0] + " " + r.ans[1]);
	  ++r.cases;
	}
  }
}

