                if (m[i] < maxDiff) {
                    s2 += m[i];
                } else {
                    s2 += maxDiff;
                }
            }
            writer.println(String.format("Case #%d: %d %s", t, s1, s2));
        }
        scanner.close();
        writer.close();
    }
