				long ans[] = new long[2];
				long maxDiff = 0;
				ans[0] = 0;
				for (int i = 1; i < r.m.length; ++i) {
				    if (r.m[i] < r.m[i - 1]) {
				        ans[0] += r.m[i - 1] - r.m[i];
				        maxDiff = (maxDiff < r.m[i - 1] - r.m[i]) ? r.m[i - 1] - r.m[i] : maxDiff;
				    }
				}
