			String result="";
			reader.readLine();
			String str[] = reader.readLine().split(" ");

			int max = Integer.MIN_VALUE;
			int result1 = 0;
			int result2 = 0;
			for(int i = 0; i<str.length-1;i++){
				int i2=Integer.parseInt(str[i+1]);
				int i1 = Integer.parseInt(str[i]);
				if((i2-i1)<0) result1+=-(i2-i1);
				if(-(i2-i1)>max) max = -(i2-i1);
			}
			for(int i = 0; i<str.length-1;i++){
				int i1 = Integer.parseInt(str[i]);
				if(i1>=max) result2 = result2+max;
				else if(i1<max) result2 = result2+i1;
			}
			writer.write("Case #"+index+": "+result1+" "+result2+"\n");
		}
		reader.close();
		writer.close();

	}

}

package googlejam1.p645;


import java.io.File;
import java.util.Scanner;

public class A
{

	//public static String fileName = "res/r1a-A-sample.in";
	//public static String fileName = "res/r1a-A-small-attempt0.in";
	//public static String fileName = "res/r1a-A-small-attempt1.in";
	public static String fileName = "res/r1a-A-large.in";


	public static void main(String[] args) throws Exception
	{
		Scanner scanner = new Scanner(new File(fileName));

		int T = scanner.nextInt();
		for (int i = 0; i < T; i++)
		{
			int N = scanner.nextInt();
			int[] in = new int[N];
			for (int j = 0; j < N; j++)
			{
				in[j] = scanner.nextInt();
			}
			long out = 0;
			int start = 0;		
			for (int i1 = 0; i1 < in.length; i1++)
			{
				if (in[i1] > start)
				{
					start = in[i1];
				}
				else
				{
					out += (start - in[i1]);
					start = in[i1];
				}
			}

			long y = out;
			// max difference is at least 1 rate
			int maxDiff = 0;
			long out1 = 0;
			for (int i2 = 0; i2 < in.length - 1; i2++)
			{
				if ( (in[i2] - in[i2+1] > maxDiff))
				{
					maxDiff = (in[i2] - in[i2+1]);
				}
			}

			long rate = maxDiff;
			for (int i1 = 0; i1 < in.length - 1; i1 ++)
			{
				out1 += Math.min(rate, in[i1]);
			}
			long z = out1;

			System.out.println("Case #" + (i+1) + ": " + y + " " + z);
		}

		scanner.close();
	}

}

package googlejam1.p255;
import java.util.StringTokenizer;
import java.io.BufferedReader;
import java.io.InputStreamReader;
public class Mushroom{
	static int[] arr = new int[1002];
	public static void main(String[] args) throws Exception{
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		StringBuilder sb = new StringBuilder();
		int T = Integer.parseInt(br.readLine().trim());
		for(int i = 1; i <= T; i++){
			int N = Integer.parseInt(br.readLine().trim());

			StringTokenizer st = new StringTokenizer(br.readLine());
			int maxDiff = 0;
			int methodA = 0;
			for(int j = 0; j < N; j++){
				arr[j] = Integer.parseInt(st.nextToken());
				if(j != 0 && arr[j] < arr[j-1]){
					methodA += (arr[j-1] - arr[j]);
					if(arr[j-1] - arr[j] > maxDiff)	maxDiff = arr[j-1] - arr[j];
				}
			}
			int methodB = 0;
			if(maxDiff != 0){
				for(int j = 0; j < N - 1; j++)
					methodB += Math.min(arr[j], maxDiff);
			}
			sb.append(String.format("Case #%d: %d %d\n", i, methodA, methodB));
		}

		System.out.print(sb);
		br.close();
	}



}

package googlejam1.p504;
