				// Array
				String[] observationStr = br.readLine().split(" ");
				Long[] observation = new Long[numObservation];
				for(int i=0; i<numObservation; i++)
				{
					observation[i] = Long.valueOf(observationStr[i]);
				}

				long ans1 = 0;
				long ans2 = 0;
				long maxDiff = 0;
				for(int i=0; i<numObservation-1; i++)
