			int z=0;
			int max=0;

			for (int i=0; i<N-1; i++){
				if (m[i]-m[i+1]>=0){
					y=y+m[i]-m[i+1];
				}
			}

			for (int i=0; i<N-1; i++){
				if (m[i]-m[i+1]>=max){
					max=m[i]-m[i+1];
				}
			}
			for (int i=0; i<N-1; i++){
				if (m[i]>=max){
					z=z+max;
				} else {
					z=z+m[i];
				}
			}
			out.println(y +" "+z); 

		}

		lin.close();
		out.close();

		}		
}

