public class MushroomMonster {
	public static void main(String[] args) throws NumberFormatException, IOException {
//		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

		BufferedReader br = new BufferedReader(new FileReader("A-large.in"));
		int cases = Integer.parseInt(br.readLine());
		for(int i =0; i<cases; i++){
			int numTimes = Integer.parseInt(br.readLine());
			String[] nums = br.readLine().split(" ");
			int[] shrooms = new int[numTimes];
			for(int j = 0; j < numTimes; j++) {
				shrooms[j] = Integer.parseInt(nums[j]);
			}
			int maxDecrease = 0;
			int case1shrooms = 0;
			int case2shrooms = 0;
			for(int j = 1; j < numTimes; j++) {
				int diff = shrooms[j-1]-shrooms[j];
//				System.out.println(diff);
				if(diff > 0) {
					case1shrooms += diff;
				}
				if(diff > maxDecrease) {
					maxDecrease = diff;
				}
			}
//			System.out.println(maxDecrease);
			for(int j = 0; j < numTimes-1; j++) {
				if(shrooms[j] < maxDecrease) {
					case2shrooms += shrooms[j];
				} else {
					case2shrooms += maxDecrease;
				}
			}
			System.out.println("Case #" + (i+1) +  ": " + case1shrooms + " " + case2shrooms);
		}
	}
}

package googlejam1.p714;


import java.io.BufferedReader;
import java.io.File;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;
import java.io.PrintWriter;

public class A {
	static BufferedReader in;
	static PrintWriter out;

	public static void main(String[] args) throws IOException {
		in = new BufferedReader(new FileReader(new File(
				"/Users/yfo/Downloads/a2.in")));
		out = new PrintWriter(new FileWriter(new File(
				"/Users/yfo/Downloads/a2.out.in")));

		int T = Integer.parseInt(in.readLine());
		for (int t = 0; t < T; t++) {

			int N = Integer.parseInt(in.readLine());
			String[] tok = in.readLine().split(" ");
			int[] res = new int[tok.length];
			for (int i1 = 0; i1 < tok.length; i1++) {
				res[i1] = Integer.parseInt(tok[i1]);
			}
			int[] m = res;

			long first = 0;
			for (int i=1;i<N;i++) {
				if (m[i]<m[i-1]) first += m[i-1] - m[i];
			}

			long max = 0;
			for (int i=0;i<N-1;i++) {
				if (m[i]>m[i+1]) {
					max = Math.max(max, m[i]-m[i+1]);
				}
			}


			long second = 0;
			for (int i=0;i<N-1;i++) {
				if (i!=N-1) {
					second += Math.min(max, m[i]);
				}

			}


			out.println("Case #" + (t + 1) + ": " + first + " "+second);
		}

		in.close();
		out.close();
	}
}

package googlejam1.p529;


import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileWriter;
import java.io.IOException;
import java.util.Scanner;

public class ProblemA {
	public static void main(String[] args){
		try {
			String input = "C:\\Users\\Qin\\Desktop\\A-large.in";
			String output = "C:\\Users\\Qin\\Desktop\\A-large.out";
			FileInputStream fis= new FileInputStream(input);
			FileWriter fw = new FileWriter(output);
			Scanner sc = new Scanner(fis);

			int num = sc.nextInt();
			System.out.println(num);
			for(int i = 0; i < num; i++){
				int N = sc.nextInt();
				int[] M = new int[N];
				int m = 0, y = 0, z = 0;
				int max = 0;

				for(int j = 0; j < N; j++)
					M[j] = sc.nextInt();

				for(int j = 1; j < N; j++){
					if(M[j - 1] > M[j]){
						if((M[j - 1] - M[j]) > max)
							max = M[j - 1] - M[j];
						y = y + (M[j - 1] - M[j]);				
					}
				}

				for(int j = 0; j < N - 1; j++){
					if(M[j] <= max){
						z = z + M[j];
					}else{
						z = z + max;
					}
				}
				fw.write("Case #" + (i + 1) + ": " + y + " " + z + "\r\n");

			}
			fis.close();
			fw.close();
		} catch (FileNotFoundException e) {
			e.printStackTrace();
		} catch (IOException e) {
			e.printStackTrace();
