import java.util.Comparator;
import java.util.HashMap;
import java.util.LinkedList;
import java.util.Scanner;

public class B {
    public static void main(String[] args) {
        Scanner sc = new Scanner(new BufferedInputStream(System.in));
        int nCases = sc.nextInt();
        sc.nextLine();
        for (int i = 0; i < nCases; i++) {
            int n = sc.nextInt();
            int totalN = n * (2 * n - 1);
            System.out.print("Case #" + String.valueOf(i + 1) + ":");
            HashMap<Integer, Integer> counter = new HashMap<>();
            for (int j = 0; j < totalN; j++) {
                int curr = sc.nextInt();
                if (counter.containsKey(curr) && counter.get(curr) == 1) {
                    counter.put(curr, 0);
                } else {
                    counter.put(curr, 1);
                }
            }
            LinkedList<Integer> list = new LinkedList<>();
            counter.forEach((h, fl) -> { if (fl > 0) list.add(h); });
            list.sort(Integer::compareTo);
            list.forEach(h -> System.out.print(" " + String.valueOf(h)));
            System.out.println();
        }
    }
}

package googlejam6.p172;
import java.io.*;
import java.util.Arrays;
import java.util.HashMap;
import java.util.HashSet;
import java.util.StringTokenizer;

public class B {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new FileReader("D:\\B-large.in"));
        PrintWriter pw = new PrintWriter(new BufferedWriter(new FileWriter("D:\\Blarge.txt")));
        int cases = Integer.parseInt(br.readLine());
        for(int t = 1; t <= cases; t++) {
            int n = Integer.parseInt(br.readLine());
            HashSet<Integer> hs = new HashSet<Integer>();
            for(int i = 0; i < 2*n-1; i++) {
                StringTokenizer st = new StringTokenizer(br.readLine());
                for(int j = 0; j < n; j++) {
                    int tmp = Integer.parseInt(st.nextToken());
                    if(hs.contains(tmp)) {
                        hs.remove(tmp);
                    }
                    else hs.add(tmp);
                }
            }
            int[] rc = new int[n];
            int index = 0;
            for(int num : hs) {
                rc[index] = num;
                index++;
            }
            Arrays.sort(rc);
            pw.print("Case #" + t + ":");
            for(int i = 0; i < n; i++) {
                pw.print(" " + rc[i]);
            }
            pw.println();
        }
        pw.close();
    }
}

package googlejam6.p391;

/**
 * https://code.google.com/codejam/contest/dashboard?c=32003#s=p0
 */
import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;
import java.util.Collections;
import java.util.HashMap;
import java.util.LinkedList;
import java.util.List;
import java.util.Map;
import java.util.Scanner;

public class RankAndFile {
	static String inputFile = "";
	static String outputFile = "";
	static Scanner in;

	static {
		outputFile = "B.out";
	}

	public static void main(String[] args) throws IOException {
		in = new Scanner(new BufferedReader((new FileReader("/Users/sandz/Downloads/B-large.in"))));
		BufferedWriter out = new BufferedWriter(new FileWriter(outputFile));
		int testCases = in.nextInt();
		StringBuilder stringBuilder = new StringBuilder();
		for (int i = 1; i <= testCases; i++) {
			int n = in.nextInt();
			Map<Integer, Integer> map = new HashMap<Integer, Integer>();
			for (int i1 = 0; i1 < 2 * n - 1; i1++) {
				for (int j = 0; j < n; j++) {
					int row = in.nextInt();
					Integer count = map.get(row);
					if (count == null) {
						count = 0;
					}
					map.put(row, ++count);
				}
			}
			List<Integer> x = new LinkedList<Integer>();
			for (Integer key : map.keySet()) {
				int count = map.get(key);
				if (count % 2 == 1) {
					x.add(key);
				}
			}
			Collections.sort(x);

			StringBuffer buffer = new StringBuffer();
			for (Integer integer : x) {
				buffer.append(integer + " ");
			}
			String ans = buffer.toString();
			stringBuilder.append("CASE #" + (i) + ": " + ans + "\n");
		}
		System.out.println(stringBuilder.toString());
		out.write(stringBuilder.toString());
		out.flush();
		in.close();
		out.close();
	}

}

package googlejam6.p318;
/**
 * File Name: Round1A2016B.java
 * Package Name: yz.google.codejam
 * Project Name: Algorithm
