    Scanner sc = new Scanner(new File("inLarge"));
    int numCases = Integer.valueOf(sc.nextLine());
    List<String> answers = new ArrayList<String>();

    cases:
    for (int test = 1; test <= numCases; test++) {
      Map<Integer, Integer> countTotal = new HashMap<>();
