				int N = Integer.parseInt(br.readLine());
				for(int i=0;i<2*N-1;i++){
					String[] numbers = br.readLine().split(" ");
					for(String tmp : numbers){
						int number = Integer.parseInt(tmp);
						count[number]++;
					}
				}

				List<Integer> list = new ArrayList<Integer>();

				for(int i=1;i<=2500;i++){
					if(count[i]==0){
						continue;
