public class BSmall {

  static final String FILENAME = "B-small-attempt1";
  static final String IN = FILENAME + ".in";
  static final String OUT = FILENAME + ".out";
  Scanner sc;
  BufferedWriter out;

  public static void main(String args[]) throws Exception {
    BSmall r = new BSmall();
	r.sc = new Scanner(new File(BSmall.IN));
	r.out = new BufferedWriter(new FileWriter(BSmall.OUT));
	int t = r.sc.nextInt();
	for (int i = 1; i <= t; i++) {
	  r.out.write("Case #" + i + ": ");
	  int n = r.sc.nextInt();
	int k = r.sc.nextInt();
	double[] p = new double[n];
	for(int i2 = 0; i2 < n; i2++) {
	  p[i2] = r.sc.nextDouble();
	}
	double ans = -1;
	for(int msk = 0; msk < (1 << n); msk++) {
	  if(Integer.bitCount(msk) != k) {
	    continue;
	  }
	  int[] idx = new int[k + 1];
	  int c = 0;
	  int l = 0;
	  while(c < n) {
	    if((msk & (1 << c)) > 0) {
	      idx[l] = c;
	      l++;
	    }
	    c++;
	  }
	  double[][] dp = new double[k + 1][k + 1];
	  for(int i1 = 1; i1 <= k; i1++) {
	    for(int j = 0; j <= k/2; j++) {
	      if(j == 0) {
	        dp[i1][j] = 1;
	        for(int x = 0; x < i1; x++) {
	          dp[i1][j] *= 1 - p[idx[x]];
	        }
	      } else if(i1 < j) {
	        dp[i1][j] = 0;
	      } else if(i1 == j) {
	        dp[i1][j] = 1;
	        for(int x = 0; x < i1; x++) {
	          dp[i1][j] *= p[idx[x]];
	        }
	      } else {
	        dp[i1][j] = dp[i1 - 1][j - 1] * p[idx[i1 - 1]] + dp[i1 - 1][j] * (1 - p[idx[i1 - 1]]);
	      }
	    }
	  }
	  ans = Math.max(ans, dp[k][k/2]);
	}
	r.out.write(Double.toString(ans));
	r.out.newLine();
	}
	r.sc.close();
	r.out.close();
  }
}

package googlejam11.p104;
import java.util.*;

class B {
  public static void main(String[] args) {
    Scanner sc = new Scanner(System.in);
    int tcc = sc.nextInt();
    for (int tc = 1; tc <= tcc; ++tc) {
      int n = sc.nextInt();
      int k = sc.nextInt();
      double[] p = new double[n];
      double[] pc = new double[k];
      for (int i = 0; i < n; ++i) p[i] = sc.nextDouble();

      double ans = 0;
      for (int bm = 0; bm < (1 << n); ++bm) {
        int bs = 0;
        for (int i = 0; i < n; ++i) if ((bm & (1 << i)) > 0) ++bs;
        if (bs != k) continue;
        bs = 0;
        for (int i = 0; i < n; ++i) if ((bm & (1 << i)) > 0) pc[bs++] = p[i];
        double[][] comp = new double[k + 1][];
        comp[0] = new double[] { 1 };
        for (int i = 1; i <= k; ++i) {
          comp[i] = new double[i + 1];
          double cp = pc[i - 1];
          for (int j = 0; j <= i; ++j) {
            if (j != 0) comp[i][j] += cp * comp[i-1][j-1];
            if (j != i) comp[i][j] += (1 - cp) * comp[i-1][j];
          }
        }
        ans = Math.max(ans, comp[k][k / 2]);
      }

      System.out.printf("Case #%d: %.6f%n", tc, ans);
    }
  }
}

