			for (int i = 0; i < N; i++)
				prob[i] = Double.parseDouble(next());

			double ans = 0;

			for (int i = 0; i < 1 << N; i++) {
				int sz = 0;
				for (int j = 0; j < N; j++) {
					if ((i & 1 << j) > 0)
						sz++;
				}
				if (sz == K) {
					double total = 0;
					for (int j = 0; j < 1 << N; j++) {
						if ((i & j) == j) {
							int sz2 = 0;
