							temp[0] = prob[0] * (1 - P[j]);
							for(int a=1;a<=K;a++)
								temp[a] = prob[a] * (1-P[j]) + prob[a-1] * P[j];
							prob = temp;
						}
					ans = Math.max(ans, prob[K/2]);
				}
			}

			out.write("Case #"+t+": ");
			out.write(""+ans);
			out.write("\n");
		}

		in .close();
		out.close();
	}
}

