                P[n] = in.nextDouble();
            }

            double maxProb = 0;

            for (int i = 0; i < (1 << N); i++) {
                int k = 0;
                for (int n = 0; n < N; n++) {
                    if ((i & 1 << n) != 0) {
                        k++;
                    }
                }
                if (k != K) continue;
                double[] p = new double[K];
                int loc = 0;
                for (int n = 0; n < N; n++) {
                    if ((i & 1 << n) != 0) {
                        p[loc] = P[n];
                        loc++;
                    }
                }

                double curProb = 0;
                for (int j = 0; j < (1 << K); j++) {
