package googlejam11.p034;
import java.io.File;
import java.io.FileNotFoundException;
import java.io.PrintStream;
import java.util.Arrays;
import java.util.Scanner;

public class RedTapeCommittee1 {


    static int n, k;
    static double[] p;
    static double[] cand;
    static double[][] f;

    public static void main(String[] args) throws FileNotFoundException {
//        Scanner cin = new Scanner(new File("B-small-attempt0.in"));
//        PrintStream cout = new PrintStream("B-small-attempt-mine.out");
        Scanner cin = new Scanner(new File("B-large.in"));
        PrintStream cout = new PrintStream("B-large.out");
//        Scanner cin = new Scanner(System.in);
//        PrintStream cout = System.out;

        int _case = 0;

        for (int T = cin.nextInt(); T > 0; T--) {
            _case++;
            StringBuilder ans = new StringBuilder();

            n = cin.nextInt();
            k = cin.nextInt();
            p = new double[n];
            cand = new double[k];
            double max = 0;
            for (int i = 0; i < n; i++) p[i] = cin.nextDouble();
            Arrays.sort(p);

            for (int x = 0; x <= k; x++) {
                int ptr = 0;
                for (int j = 0; j < x; j++) cand[ptr++] = p[j];
                for (int j = n - 1; j >= n + x - k; j--) cand[ptr++] = p[j];

                f = new double[k][k + 1];
                f[0][0] = 1 - cand[0];
                f[0][1] = cand[0];
                for (int i = 1; i < k; i++)
                    for (int j = 0; j <= k; j++) {
                        f[i][j] = (1 - cand[i]) * f[i - 1][j]; // vote for "NO"
                        if (j > 0) f[i][j] += cand[i] * f[i - 1][j - 1]; // vote for "YES"
                    }
                max = Math.max(max, f[k - 1][k / 2]);
            }
            ans.append(String.format("%.10f", max));

            cout.printf("Case #%d: %s%n", _case, ans.toString());
        }

        cin.close();
        cout.close();
    }
}

package googlejam11.p096;
import java.io.File;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.Scanner;

public class RedTape {
	public static void main(String [] args) throws IOException {
		Scanner input = new Scanner(new File("input.txt"));
		PrintWriter out = new PrintWriter(new File("output.txt"));

		int max = input.nextInt();
		for(int c = 1; c <= max; c++) {
			int size = input.nextInt();
			int comm = input.nextInt();

			double[] arr = new double[size];
			for(int j = 0; j < size; j++)
				arr[j] = input.nextDouble();
			Arrays.sort(arr);
			double maxV = 0;
			for(int cut = 0; cut <= comm; cut++) {
				double[] a = new double[comm];
				for(int j = 0; j < cut; j++)
					a[j] = arr[j];
				for(int j = 0; j < comm - cut; j++)
					a[j + cut] = arr[size - j - 1];

				double[][] prob = new double[201][201];
				int tie = 100;
				prob[0][tie-1] = a[0];
				prob[0][tie+1] = 1 - a[0];
				for(int i = 1; i < comm; i++)
					for(int j = tie - i - 1; j <= tie + i + 1; j++)
						if(j > 0 && j < 200)
							prob[i][j] = prob[i-1][j+1] * a[i] + prob[i-1][j-1] * (1 - a[i]);
				maxV = Math.max(maxV, prob[comm-1][tie]);
			}
			out.printf("Case #%d: %.10f%n", c, maxV);
		}
		out.close();
	}
}

