                    L[i] = min(L[i], S[i]);
                    H[i] = max(H[i], S[i]);
                }

                int a = H[i] - D;
                int r = L[i];
                //System.out.println(i+" "+a+" "+r);
                if (a <= r) {
                    add.add(a);
                    remove.add(r);
                }
            }
            Collections.sort(add);
            Collections.sort(remove);
            int aat = 0;
            int rat = 0;
            int total = 0;
            int ans = 0;
            while (aat < add.size() && rat < remove.size()) {
                if (add.get(aat) <= remove.get(rat)) {
                    total++;
                    aat++;
                } else {
                    total--;
                    rat++;
                }
                ans = max(ans, total);
            }
            System.out.format("Case #%d: %d\n", zz, ans);
        }
    }
}

