public class Poetry {
  public static void main(String[] args) {
    BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
    BufferedWriter bw = new BufferedWriter(new OutputStreamWriter(System.out));

    try {
      int t = Integer.parseInt(br.readLine());
      for (int i = 1; i <= t; i++) {
        int n = Integer.parseInt(br.readLine());

        int[] nums = new int[n+1];
        for (int j = 1; j <= n; j++) {
          nums[j] = n-j+1;
        }
        nums[n] = 1;
        for (int j = n-1; j >= 1; j--) {
          int j1 = j;
			int r = 0;
			while (j1 > 0) {
			  r *= 10;
			  r += j1 % 10;
			  j1 /= 10;
			}
		int reverse_j = r;
          if (reverse_j <= n) {
            nums[j] = 1 + Math.min(nums[reverse_j],nums[j+1]);
          } else {
            nums[j] = 1 + nums[j+1];
          }
        }

        bw.write("Case #" + i + ": " + nums[1]);
        bw.newLine();
      }

      bw.flush();
      bw.close();
      br.close();
    } catch (Exception e) {
      e.printStackTrace();
    }
  }
}

package googlejam2.p301;
import java.util.Scanner;


public class Problem1 {


	public static void main(String[] args) {

		int maxN = 1000000;
		int[] minCount = new int[maxN+1]; // minCount[i] is for counting from 1 to i
		minCount[1] = 1;

		int reverseN;
		for(int N=2; N<maxN+1; N++){
			int input = N;
			int reversedNum = 0;
			while (input != 0) {
			    reversedNum = reversedNum * 10 + input % 10;
			    input = input / 10;   
			}
			reverseN = reversedNum;
			if((N%10 != 0) && reverseN<N){
				minCount[N] = Math.min(1+minCount[N-1], 1+minCount[reverseN]);
			} else {
				minCount[N] = 1+minCount[N-1];
			}
		}

		Scanner scan = new Scanner(System.in);
		int T = scan.nextInt();
		scan.nextLine();

		for(int caseNb=0; caseNb<T; caseNb++){
			int N = scan.nextInt();
			scan.nextLine();
			System.out.println("Case #"+(caseNb+1)+": "+minCount[N]);
		}
	}

}

