public class A {

	static Scanner reader;
	static PrintWriter writer;

	public static void main(String[] args) throws IOException {
		reader = new Scanner(new FileReader("input"));
		writer = new PrintWriter(new FileWriter("output"));
		int nt = reader.nextInt();
		for(int tc = 1; tc <= nt; tc++) {
			int n = reader.nextInt();
			Queue<Integer> Q = new LinkedList<>();
			Q.add(0);
			int[] d = new int[n + 1];
			Arrays.fill(d, Integer.MAX_VALUE);
			d[0] = 0;
			while(d[n] == Integer.MAX_VALUE) {
				int cur = Q.poll();
				if(cur + 1 <= n && d[cur + 1] == Integer.MAX_VALUE) {
					d[cur + 1] = 1 + d[cur];
					Q.add(cur + 1);
				}
				StringBuilder sb = new StringBuilder();
				sb.append(cur);
				sb = sb.reverse();
				int r = Integer.parseInt(sb.toString());
				if(r <= n && d[r] == Integer.MAX_VALUE) {
					d[r] = 1 + d[cur];
					Q.add(r);
				}
			}
			writer.printf("Case #%d: %d\n", tc, d[n]);
		}
		writer.close();
		reader.close();
	}

}

package googlejam2.p178;
import java.io.File;
import java.io.FileNotFoundException;
import java.io.PrintWriter;
import java.util.LinkedList;
import java.util.Queue;
import java.util.Scanner;

public class A {

  public static void main(String[] args) throws FileNotFoundException {
    Scanner in = new Scanner(new File("A-small-attempt0.in"));
    PrintWriter out = new PrintWriter(new File("A-small-attempt0.out"));

    int T = in.nextInt();
    for (int t = 1; t <= T; t++) {

      int n = in.nextInt();
      int dp[] = new int[n+1];
      dp[1] = 1;
      Queue<Integer> q = new LinkedList<Integer>();
      q.add(1);

      while(!q.isEmpty()) {
        int at = q.poll();
        int moves = dp[at];
        if (at == n) break;

        int next = at + 1;
        if (next <= n && dp[next] == 0) {
          dp[next] = moves + 1;
          q.add(next);
        } 
        int tmp = at;
        next = 0;
        while (tmp > 0) {
          next = next*10 + tmp % 10;
          tmp /= 10;
        }
        if (next <= n && dp[next] == 0) {
          dp[next] = moves + 1;
          q.add(next);
        } 
      }

      out.println("Case #" + t + ": " + dp[n]);
    }

    out.close();
  }

}

