		{
			long first = dp[i-1] + 1;
			char[] test = String.valueOf((long) i).toCharArray();
			long res = 0;
			long mult = 1;
			for(int i1 = 0; i1 < test.length; ++i1)
			{
				int tmp = Integer.valueOf(String.valueOf(test[i1]));
				if(i1 == test.length-1 && tmp == 0)
				{
					// es darf kein digit verloren gehen...
					res += 10000000000L;
				}
				res += tmp * mult;
				mult *= 10;
			}
			long index = res;
			long second = 0;
			if(index >= 1 && index < i)
			{
				second = dp[(int)index]+1;
			}
			if(second > 1 && second < first)
			{
				dp[i] = second;
			}else
			{
				dp[i] = first;
			}
		}


		for(int numC = 0; numC < numCases; ++numC)
		{

			long n = s.nextLong();


			System.out.print("Case #");
			System.out.print(numC+1);
			System.out.print(": ");
			System.out.println(dp[(int)n]);
		}

	}
}

package googlejam2.p134;


import java.util.Scanner;

public class R1BA {


	public static void main(String[] args){
		Scanner in = new Scanner(System.in);
		int t = in.nextInt();
		for(int i=1; i<=t; i++){
			long n = in.nextLong();
			long k = n;
			int log = 1; //log is num of digits
			long totalPower = 1;
			while(k/10>0){
				log++;
				k=k/10;
				totalPower = totalPower*10;
			}

			long total = 0;
			if(log == 1){
				total = n;
			} else {
				total = 10;
				long power = 1;
				for(int j = 2; j<= log-1; j++){
					if(j%2==0) power = power*10;
					total+=power; //now we're at 999900001
					total+=(1+(j%2)*9)*power-1;
				}
				//last power of 10...
				if(log%2==0) power=power*10;
				if(n!=totalPower){
					long rem = n%(power*(1+(log%2)*9)); //last (log+1)/2 digits 
					k = n/(power*(1+(log%2)*9));

					if(rem==0){
						k--;
						rem = power*(1+(log%2)*9);
					}
					if(k<=power/10){
						total+=n-totalPower;
					} else {
						long power2 = power/10;
						while(k>0){
							total+=power2*(k%10);
							power2=power2/10;
							k=k/10;
						}
						total+=rem;
					}
				}

			}


			System.out.print("Case #" + i + ": ");
			System.out.print(total);
			System.out.println();
		}

		in.close();
	}
}

package googlejam2.p214;


import java.io.File;
import java.io.IOException;
import java.io.PrintStream;
import java.util.HashMap;
import java.util.Map.Entry;
import java.util.Scanner;
import java.util.TreeMap;

// Using the Google Guava library.
public class A {

	public static void main(String [] args) throws IOException {
		String inputFile = "src/A-small-1.in";
		Scanner in = new Scanner(new File(inputFile));
		PrintStream out = new PrintStream(inputFile.substring(0, inputFile.length()-2)+"out");
		int cases = in.nextInt();
		for (int cs = 1; cs <= cases; cs++) {
			long n = in.nextLong();
			A a = new A(n);
			String ans = "Case #"+cs+": "+a.minSteps;
			out.println(ans);
			System.out.println(ans);
		}
		out.close();
		in.close();
	}

	TreeMap<Long, Integer> queue = new TreeMap<>();
	HashMap<Long,Integer> seen = new HashMap<>();
	int minSteps = Integer.MAX_VALUE;

	public A(long n) {
	    queue.put(n, 1);
	    while(!queue.isEmpty()) {
	        Entry<Long, Integer> e = queue.pollFirstEntry();
	        long v = e.getKey();
	        int s = e.getValue();
	        if (v == 1) {
	            minSteps = Math.min(s, minSteps);
	            continue;
	        }
	        if (s >= minSteps) { continue; }
	        {
	            Integer ps = seen.get(v-1);
	            if (ps == null || ps > s+1) {
	                queue.put(v-1, s+1);
	                seen.put(v-1, s+1);
	            }
            }
	        if (v % 10 != 0) {
	            long vr = Long.parseLong(new StringBuilder(Long.toString(v)).reverse().toString());
	            if (vr < v){
	                Integer ps = seen.get(vr);
	                if (ps == null || ps > s+1) {
	                    queue.put(vr, s+1);
	                    seen.put(vr, s+1);
	                }
	            }
	        }
	    }
	}
}

