        		pw.println("Case #"+i+": IMPOSSIBLE");
        		continue;
        	}
        	pw.println("Case #"+i+": POSSIBLE");
        	if(BigInteger.valueOf(M).compareTo(x.pow(B-2))==0){
        		String s = Long.toString(M-1, 2);
        		int le = s.length();
        		for(int b=0;b<B-le-2;b++)
        			s = "0"+s;
        		pw.println("0"+(M==1?"":s)+"1");
        	}
