                                answer += "0";
                            }
                        } else {
                            if ( i<j){
                                answer = answer + "1";
                            } else {
                                answer = answer + "0";
                            }
                        }
                    }
                    if ( i<b-1) {
                        answer += "\n";
                    }
                }
            }

            System.err.println("Case #"+test + ": " + b + " " + (m.add( BigInteger.ONE )));
            System.err.println("Case #"+test + ": " + answer );
            System.out.println("Case #"+test + ": " + answer );
        }
