				if (pow+2 > n)
					ok = false;
				else {
					for (int i = 1; i <= pow+1; i++) {
						for (int j = 1; j < i; j++) {
							a[j][i] = 1;
						}
					}
					for (int i = 1; i <= pow+1; i++) {
						a[i][n] = 1;
					}
				}
			}
			else {
				for (int i = 1; i < n; i++) {
					for (int j = 1; j < i; j++) {
						a[j][i] = 1;
					}
				}
				for (int i = 0; i < 60; i++) {
					if ((m & (1l << i)) != 0) {
